style.css:
@-moz-document url-prefix("chrome://stylish-custom/") {
toolbarbutton{-moz-appearance:none!important;-moz-box-align:center!important;-moz-box-pack:center!important;margin:0!important;border:1px solid black!important;border-top:2px solid black!important;-moz-border-top-colors:black #393939!important;-moz-border-right-colors:none!important;-moz-border-bottom-colors:none!important;-moz-border-left-colors:none!important;padding:3px!important;background-color:transparent!important;border-radius:3px!important;-moz-background-clip:padding!important;background:url("chrome://global/skin/button/outer-gradient-bg.png") top left repeat-x #070707!important;color:#bbb!important}
.toolbarbutton-icon{-moz-margin-end:5px!important}
.toolbarbutton-text{margin:0!important;text-align:center!important}
toolbarbutton:focus{outline:1px dotted #ccc!important;outline-offset:-2px!important}
toolbarbutton-box{border:1px solid transparent!important;padding-top:2px!important;-moz-padding-end:3px!important;padding-bottom:3px!important;-moz-padding-start:4px!important;margin:0!important;background:url("chrome://global/skin/button/outer-gradient-bg.png") top left repeat-x #070707!important;-moz-background-origin:border!important}
toolbarbutton-text{margin:0!important;text-align:center!important}
toolbarbutton[disabled="true"]>.toolbarbutton-box{padding-top:2px!important;-moz-padding-end:3px!important;padding-bottom:3px!important;-moz-padding-start:4px!important}
toolbarbutton[type="menu-toolbarbutton"]{-moz-box-align:center!important;-moz-box-pack:center!important;margin:0;border:none!important}
toolbarbutton-menu-dropmarker,.toolbarbutton-menubutton-dropmarker{margin:1px!important;background-image:url("chrome://global/skin/dropmarker/arrow-dropmarker-dn.png")!important;background-repeat:no-repeat!important;background-position:center center!important;min-width:11px!important;min-height:11px!important;list-style-image:none!important}
toolbarbutton:hover .toolbarbutton-menu-dropmarker,toolbarbutton:hover .toolbarbutton-menubutton-dropmarker,toolbarbutton:active .toolbarbutton-menu-dropmarker,toolbarbutton:active toolbarbutton-menubutton-dropmarker{background-image:url("chrome://global/skin/dropmarker/arrow-dropmarker-dn-hov.png")!important}
toolbarbutton-menu-dropmarker[disabled="true"],.toolbarbutton-menubutton-dropmarker[disabled="true"]{background-image:url("chrome://global/skin/dropmarker/arrow-dropmarker-dn-dis.png")!important}
toolbarbutton-menubutton-dropmarker[open="true"]{margin-top:2px!important;margin-bottom:0!important;-moz-margin-start:2px!important;-moz-margin-end:0!important}
toolbarbutton.plain{border:0!important;margin:0!important;padding:0!important;background-image:none!important;background-color:transparent!important}
toolbarbutton.plain>.toolbarbutton-box{background-image:none!important;background-color:transparent!important}
toolbarbutton:hover:not([disabled="true"]){-moz-border-top-colors:black #575757!important;-moz-border-right-colors:none!important;-moz-border-bottom-colors:none!important;-moz-border-left-colors:none!important;border-color:black!important;color:white!important;background:url("chrome://global/skin/button/outer-gradient-hover-bg.png") top left repeat-x #2f2f2f!important}
toolbarbutton:hover:active:not([disabled="true"]){border-color:#FFC834!important;-moz-border-top-colors:#FFC834 #393939!important;-moz-border-right-colors:none!important;-moz-border-bottom-colors:none!important;-moz-border-left-colors:none!important;color:white!important;background:url("chrome://global/skin/button/outer-gradient-hover-bg.png") top left repeat-x #545454!important}
toolbarbutton[disabled="true"]{color:#444!important}
toolbarbutton[open="true"],toolbarbutton[checked="true"]{border-color:black!important}
toolbarbutton:hover:not([disabled="true"])>.toolbarbutton-box{background:url("chrome://global/skin/button/outer-gradient-hover-bg.png") top left repeat-x #2f2f2f!important;-moz-background-origin:border!important}
toolbarbutton:hover:active:not([disabled="true"])>.toolbarbutton-box,toolbarbutton[open="true"]:not([disabled="true"])>.toolbarbutton-box,toolbarbutton[checked="true"]:not([disabled="true"])>.toolbarbutton-box{background:url("chrome://global/skin/button/outer-gradient-hover-bg.png") top left repeat-x #545454!important;-moz-background-origin:border!important}
toolbarbutton:focus:not([disabled="true"])>.toolbarbutton-box,toolbarbutton[default="true"]:not([disabled="true"])>.toolbarbutton-box{outline:1px dotted #ccc!important;outline-offset:-3px!important}
/*add bg and selected colours*/
treechildren:-moz-tree-cell-text{color:#fff!important}
treechildren:-moz-tree-row(even){background-color:#111!important}
treechildren:-moz-tree-row(odd){background-color:#000!important}
treechildren:-moz-tree-row(selected,focus){color:rgb(0,173,238)!important}
treechildren:-moz-tree-row(selected){background-color:rgba(0,0,0,.25)!important}
}
