style.css:
/* Base Styles */
body {
    font-family: Arial, sans-serif;
    color: #333;
    background: #f4f4f4;
    margin: 0;
    padding: 0;
}

/* Header Styles */
header {
    background: #ff69b4;
    color: #fff;
    text-align: center;
    padding: 1rem;
}

header h1 {
    margin: 0;
    font-size: 2rem;
}

/* Navigation Menu */
nav {
    background: #ff1493;
    text-align: center;
    padding: 0.5rem 0;
}

nav a {
    color: #fff;
    text-decoration: none;
    margin: 0 1rem;
}

nav a:hover {
    color: #ff69b4;
}

/* Main Content */
main {
    max-width: 800px;
    margin: 2rem auto;
    padding: 1rem;
    background: #fff;
    border-radius: 8px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
}

section h2 {
    color: #ff1493;
    font-size: 1.5rem;
    margin-bottom: 1rem;
}

section p {
    line-height: 1.6;
    margin-bottom: 1rem;
}

/* Footer Styles */
footer {
    background: #ff1493;
    color: #fff;
    text-align: center;
    padding: 1rem;
    margin-top: 2rem;
}
