style.css:
@-moz-document domain("code.amazon.com") {
body {
    background: #222  !important;
  }

  body > * {
    filter: invert(100%) hue-rotate(180deg) brightness(135%) !important;
  }
  
  a {
    color: #0af !important;
  }
  
  .star-light-pink { 
    filter: hue-rotate(235deg) brightness(175%) !important;
  }
  
  li.active a {
    color: #000 !important;
  }
  
  a.btn-primary {
    color: #fff !important;
  }
  
  .code {
    filter: brightness(90%) saturate(300%) !important;
  }
  
  .commit-description {
    color: #fff;
  }
  
  .edit textarea {
    background: #eee !important;
    border: none !important;
  }
  
  .badges {
    filter: invert(100%) hue-rotate(180deg) brightness(115%) !important;
  }
  
  img[alt="Phone image"] {
    filter: invert(100%) hue-rotate(180deg) !important;
  }
  
  .badges .label, .badges, .badge {    
    text-shadow: 1px 1px 0 #000 !important;
  }
  
  .is-it-down-stripe {
    top: 0 !important;
  }
}
