style.css:
body {
  background-color: #f0f0f0; /* Light gray background */
  background-image: linear-gradient(135deg, #70a1ff, #b8e994); /* Gradient from blue to green */
  background-size: 400% 400%; /* Larger background size for gradient animation */
  animation: gradientAnimation 15s ease infinite; /* Animation for gradient */
}

@keyframes gradientAnimation {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

/* Optional: Add some opacity to the gradient */
/* Adjust the opacity to your preference */
body::after {
  content: "";
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(255, 255, 255, 0.1); /* Adjust opacity here */
  pointer-events: none; /* Ensure clicks go through */
}
