style.css:
/* Regex: ^https:\/\/www.crave.ca\/en\/(movies|tv-shows)\/([a-z0-9\-]+)\/([a-z0-9\-]+)$ */
body {
  background-color: black;
}

header {
  position: fixed;
  top: 0 !important;
  left: 0;
  width: 100%;
  background-color: rgba(0,0,0,0) !important;
  transition: 0.5s;
  z-index: 1000;
}

header:hover {
  background-color: rgba(0,0,0,1) !important;
}

header > * {
  transition: 0.5s;
  opacity: 0;
}

header:hover > * {
  opacity: 1;
}

main {
  padding-top: 0 !important;
}

main > div {
  flex-grow: 1;
  display: flex;
  flex-direction: column;
}

main > div > h1,
main > div > div:nth-of-type(2),
main > div > div:nth-of-type(1) > nav,
footer {
  display: none;
}

main > div > div:nth-of-type(1) {
  display: flex;
  margin: 0;
  flex-grow: 1;
}

main > div > div:nth-of-type(1) > div {
  width: 100%;
  flex-direction: column;
  justify-content: center;
}

div[data-testid="videoplayer"] {
  width: 100%;
  max-height: 100vh;
}

div[data-testid="videoplayer"] > div {
  max-height: 100%;
}
