style.css:
@-moz-document url-prefix("chrome://newtab/") {
/* Reset some default styles */
    body, h1, p {
        margin: 0;
        padding: 0;
        font-family: 'Arial, sans-serif';
    }

    /* Body styling */
    body {
        background-color: #ffe4e1 !important; /* Light pink background */
        color: #333 !important;
        text-align: center;
        padding: 20px !important;
    }

    /* Header styling */
    header {
        background-color: #ff69b4 !important; /* Hot pink */
        color: white !important;
        padding: 20px 0 !important;
        margin-bottom: 20px !important;
    }

    /* Main content styling */
    main {
        background: white !important;
        padding: 20px !important;
        border-radius: 10px !important;
        box-shadow: 0 4px 8px rgba(0,0,0,0.1) !important;
        max-width: 600px !important;
        margin: 0 auto !important;
    }

    /* Footer styling */
    footer {
        margin-top: 20px !important;
        color: #ff69b4 !important;
    }

    /* Add Hello Kitty image as background */
    body::before {
        content: '' !important;
        background-image: url('https://i58.servimg.com/u/f58/12/69/49/80/wall14.png') !important;
        background-size: cover !important;
        background-position: center !important;
        opacity: 0.1 !important;
        position: fixed !important;
        top: 0 !important;
        left: 0 !important;
        width: 100% !important;
        height: 100% !important;
        z-index: -1 !important;
    }
}
