style.css:
@-moz-document domain("mail.google.com") {
.y6 .bqe, .y6 .bq4, .y2 {
        display: none !important;
    }

/* Κρύβουμε τη μπάρα αρχικά */
.aeN {
  width: 0;
  overflow: hidden;
  transition: width 0.3s; /* Προσαρμόστε το χρόνο όπως θέλετε */
}

/* Περιοχή ενεργοποίησης */
.sidebar-trigger-area {
  position: absolute;
  left: 0;
  width: 3px;
  height: 100%;
  z-index: 1000;
}

/* Όταν η μπάρα είναι ανοικτή */
.aeN.open {
  width: 187px; /* Προσαρμόστε το πλάτος όπως θέλετε */
}
}
