style.css:
@-moz-document domain("mail.google.com") {
.y6 .bqe, .y6 .bq4, .y2 {
        display: none !important;
    }

/* Κρύψε το αριστερό πλαϊνό (navigator) */
.ae4 {
    display: none;
}

/* Hot zone για το αριστερό πλαϊνό */
.left-hover-zone {
    position: fixed;
    top: 0;
    left: 0;
    width: 20px;
    height: 100%;
    z-index: 1000;
}

/* Εμφάνιση του navigator όταν το ποντίκι είναι πάνω στην hot zone */
.left-hover-zone:hover ~ .ae4 {
    display: block;
    position: fixed;
    left: 0;
    width: 200px;
    height: 100%;
    background: white;
    box-shadow: 2px 0px 5px rgba(0, 0, 0, 0.5);
}

/* Κρύψε το δεξιό πλαϊνό (tablist) */
.bqL {
    display: none;
}

/* Hot zone για το δεξιό πλαϊνό */
.right-hover-zone {
    position: fixed;
    top: 0;
    right: 0;
    width: 20px;
    height: 100%;
    z-index: 1000;
}

/* Εμφάνιση του tablist όταν το ποντίκι είναι πάνω στην hot zone */
.right-hover-zone:hover ~ .bqL {
    display: block;
    position: fixed;
    right: 0;
    width: 200px;
    height: 100%;
    background: white;
    box-shadow: -2px 0px 5px rgba(0, 0, 0, 0.5);
}
}
