style.css:
@-moz-document domain("lichess.org") {
/* Victory message for winning a game */
  .rplayer-won .result .rclock {
    position: relative;
  }

  .rplayer-won .result .rclock::after {
    content: "Victory";
    font-size: 2em;
    color: green;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background: rgba(255, 255, 255, 0.8);
    padding: 0.5em 1em;
    border-radius: 10px;
    pointer-events: none;
  }

  /* Fatality message for losing a game */
  .rplayer-lost .result .rclock {
    position: relative;
  }

  .rplayer-lost .result .rclock::after {
    content: "Fatality";
    font-size: 2em;
    color: red;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    background: rgba(255, 255, 255, 0.8);
    padding: 0.5em 1em;
    border-radius: 10px;
    pointer-events: none;
  }
}
