style.css:
@-moz-document domain("lichess.org") {
/* Define the Fatality message style */
  .fatality-message {
    display: none;
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    font-size: 3em;
    color: red;
    text-shadow: 2px 2px 5px black;
    z-index: 9999; /* Ensure the message is on top */
    animation: fatality-animation 3s ease-in-out forwards;
  }

  /* Animation keyframes */
  @keyframes fatality-animation {
    0% {
      opacity: 0;
      transform: translate(-50%, -50%) scale(0.5);
    }
    50% {
      opacity: 1;
      transform: translate(-50%, -50%) scale(1.1);
    }
    100% {
      opacity: 0;
      transform: translate(-50%, -50%) scale(1);
    }
  }

  /* Target the game result loss element */
  .status.finished .result.loss + .game-result:after {
    content: "Fatality";
    display: block;
  }
}
