style.css:
@keyframes colorFade {
  0% {
    background-color: #8B0000; /* Dark red */
  }
  50% {
    background-color: #FF0000; /* Bright red */
  }
  100% {
    background-color: #8B0000; /* Dark red */
  }
}

body {
  animation: colorFade 5s infinite alternate; /* Adjust duration and other properties as needed */
  background-size: 10px 10px; /* Pixel size */
  background-image: linear-gradient(45deg, #000000 25%, transparent 25%, transparent 75%, #000000 75%);
}
