style.css:
@-moz-document domain("nzbs.in") {
/* 
 * Name: Newzburners Dark
 * Version: 20210922
 * Description: Dark theme for nzb.in
 * Requirements:
 *     - Stylus addon.
 *     - nzb.in own dark theme enabled. Edit your settings in My Stuff/My Profile menu.
 */

:root {
    /* Background accent */
    --main-accent0-hue: 240;
    /* Foreground accent */
    --main-accent1-hue: 175;

    /* Colors */
    --base-color0-sat: 6%;
    
    --color0-light0: hsl(var(--main-accent0-hue), var(--base-color0-sat), 32%);
    --color0-normal: hsl(var(--main-accent0-hue), var(--base-color0-sat), 30%);
    --color0-dark0: hsl(var(--main-accent0-hue), var(--base-color0-sat), 26%);
    --color0-dark1: hsl(var(--main-accent0-hue), var(--base-color0-sat), 24%);
    --color0-dark2: hsl(var(--main-accent0-hue), var(--base-color0-sat), 22%);
    --color0-dark3: hsl(var(--main-accent0-hue), var(--base-color0-sat), 20%);
    --color0-dark4: hsl(var(--main-accent0-hue), var(--base-color0-sat), 18%);
    --color0-dark5: hsl(var(--main-accent0-hue), var(--base-color0-sat), 16%);
    --color0-darkest: hsl(var(--main-accent0-hue), var(--base-color0-sat), 9%);

    --base-color1-sat: 45%;
    --color1-light: hsl(var(--main-accent1-hue), var(--base-color1-sat), 70%);
    --color1-normal: hsl(var(--main-accent1-hue), var(--base-color1-sat), 55%);
    --color1-dark1: hsl(var(--main-accent1-hue), var(--base-color1-sat), 45%);
    --color1-dark2: hsl(var(--main-accent1-hue), var(--base-color1-sat), 30%);

    --base-color2to7-sat: 72%;

    --base-color-hue-2: 270;
    --color2-normal: hsl(var(--base-color-hue-2), var(--base-color2to7-sat), 65%);
    
    --base-color-hue-3: 210;
    --color3-normal: hsl(var(--base-color-hue-3), var(--base-color2to7-sat), 65%);

    --base-color-hue-4: 150;
    --color4-normal: hsl(var(--base-color-hue-4), var(--base-color2to7-sat), 65%);
    --color4-dark1: hsl(var(--base-color-hue-4), var(--base-color2to7-sat), 40%);

    --base-color-hue-5: 60;
    --color5-normal: hsl(var(--base-color-hue-5), var(--base-color2to7-sat), 65%);

    --base-color-hue-6: 30;
    --color6-normal: hsl(var(--base-color-hue-6), var(--base-color2to7-sat), 65%);
    --color6-dark1: hsl(var(--base-color-hue-6), var(--base-color2to7-sat), 45%);

    --base-color-hue-7: 0;
    --color7-normal: hsl(var(--base-color-hue-7), var(--base-color2to7-sat), 65%);
    --color7-dark1: hsl(var(--base-color-hue-7), var(--base-color2to7-sat), 45%);
    
    --base-color-hue-8: 0;
    --color8-lightest: hsl(var(--base-color-hue-8), 0%, 100%);
    --color8-normal: hsl(var(--base-color-hue-8), 0%, 82%);
    --color8-dark1: hsl(var(--base-color-hue-8), 0%, 73%);
    --color8-dark2: hsl(var(--base-color-hue-8), 0%, 60%);
    --color8-dark3: hsl(var(--base-color-hue-8), 0%, 53%);
    --color8-dark4: hsl(var(--base-color-hue-8), 0%, 40%);

    /* Background colors */
    --bg0-light0: var(--color0-light0);
    --bg0-normal: var(--color0-normal);
    --bg0-dark0: var(--color0-dark0);
    --bg0-dark1: var(--color0-dark1);
    --bg0-dark2: var(--color0-dark2);
    --bg0-dark3: var(--color0-dark3);
    --bg0-dark4: var(--color0-dark4);
    --bg0-dark5: var(--color0-dark5);
    --bg0-darkest: var(--color0-darkest);
    
    --bg1-dark1: var(--color1-dark1);
    --bg1-dark2: var(--color1-dark2);

    --bg4-dark1: var(--color4-dark1);
        
    --bg6-dark1: var(--color6-dark1);
    
    --bg7-normal: var(--color7-normal);
    
    --bg8-dark1: var(--color8-dark1);
    --bg8-dark2: var(--color8-dark2);

    /* Border colors */
    --border0-normal: var(--color0-normal);
    --border0-dark0: var(--color0-dark0);
    --border0-dark3: var(--color0-dark3);
    --border0-dark4: var(--color0-dark4);
    --border0-dark5: var(--color0-dark5);

    --border3-normal: var(--color3-normal);

    --border4-normal: var(--color4-normal);

    --border5-normal: var(--color5-normal);

    --border6-normal: var(--color6-normal);

    --border7-normal: var(--color7-normal);
    --border7-dark1: var(--color7-dark1);
    
    --border8-dark2: var(--color8-dark2);
    --border8-dark4: var(--color8-dark4);
}

/*** style-min.css ***/
.site a {
	color: var(--color1-normal);
}
.site a:hover {
	color: var(--color1-dark1);
}
.menu-toggle, input[type="submit"], button, input[type="button"], input[type="button"],
article.post-password-required input[type=submit], li.bypostauthor cite span {
	color: var(--color8-normal);
	background-color: var(--bg0-normal);
	background-image: none;
	border: 1px solid var(--border0-normal);
	border-radius: 2px;
	box-shadow: none;
}
.menu-toggle:hover, button:hover, input[type="submit"]:hover,
input[type="button"]:hover, input[type="reset"]:hover,
article.post-password-required input[type=submit]:hover {
	color: var(--color1-normal);
	background-color: var(--bg0-light0);
	background-image: none;
	border: 1px solid var(--border0-light1);
}
.menu-toggle:active, .menu-toggle.toggled-on, button:active,
input[type="submit"]:active, input[type="button"]:active,
input[type="reset"]:active {
	color: var(--color1-dark1);
	background-color: var(--bg0-normal);
	background-image: none;
	border: 1px solid var(--border0-normal);
	box-shadow: none;
}
.entry-content img {
	box-shadow: none;
    -webkit-filter: invert(50%);
    filter: invert(50%);
}
.site {
    background-color: var(--bg0-dark5);
}
.site-header h1 a, .site-header h2 a {
	color: var(--color8-dark2);
}
.site-header h1 a:hover, .site-header h2 a:hover {
	color: var(--color1-dark1);
}
.main-navigation ul.nav-menu,
.main-navigation div.nav-menu>ul {
   border-bottom: 1px solid #757575;
   border-top: 1px solid #757575;
}
.main-navigation li a {
   color: var(--color8-normal);
}
.main-navigation li a:hover {
   color: var(--color1-dark1);
}
.main-navigation .current-menu-item>a,
.main-navigation .current-menu-ancestor>a,
.main-navigation .current_page_item>a,
.main-navigation .current_page_ancestor>a {
	color: var(--color8-dark3);
}
.widget-area .widget a {
	color: var(--color8-dark2);
}
.widget-area .widget a:hover {
	color: var(--color1-dark1);
}
footer[role="contentinfo"] {
	border-top: 1px solid var(--border7-dark1);
}
footer[role="contentinfo"] a {
	color: var(--color8-dark2);
}
footer[role="contentinfo"] a:hover {
	color: var(--color1-dark1);
}
.entry-header .entry-title a {
	color: var(--color1-dark1);
}
.entry-header .comments-link {
	color: var(--color8-dark2);
}
.comments-link a, .entry-meta a {
	color: var(--color8-dark2);
}
.comments-link a:hover, .entry-meta a:hover {
	color: var(--color1-dark);
}
.site-content article {
	border-bottom: 4px double #757575;
}
body .site {
	box-shadow: none;
}

/*** style.css ***/
html {
	background-color: var(--bg0-darkest);
}
body {
	background-color: var(--bg0-darkest);
	color: var(--color8-normal);
}
iframe {
    border: none;
}
::-webkit-scrollbar {
    border: none;
}
::-webkit-scrollbar-track {
	background-color: #0F0F12;
}
::-webkit-scrollbar-thumb {
	background-color: var(--bg0-normal);
}
select {
	color: #222;
    border: none;
	box-shadow: none;
}
select[multiple=multiple] {
    padding: 0px;
}
option {
	background-color: var(--bg0-darkest);
    color: var(--color8-normal);
}
input[type=text], input[type=password], input[type=email], textarea {
	background: #0F0F12 !important;
	border: 1px solid #0A0A0D !important;
	color: var(--color8-normal) !important;
	border-radius: 2px;
	box-shadow: none !important;
}
input[type=checkbox], input[type=radio], select:not([multiple]) {
    -webkit-filter: invert(95%) hue-rotate(138deg) brightness(80%);
	filter: invert(95%) hue-rotate(138deg) brightness(80%);
}
b {
	color: var(--color1-normal);
}
.rel {
	color: var(--color1-normal);
    font-size: 14px;
}
.cat {
	color: var(--color6-normal);
}
.files {
	color: var(--color2-normal);
}
.stats {
	color: var(--color7-normal);
}
.rates {
	color: var(--color3-normal);
}
#logo .logolink {
	color: var(--color1-dark1);
}
.footer {
	background: var(--bg0-darkest);
	border-top: 1px solid var(--border7-normal);
}
table.data th {
	background-color: var(--bg0-normal);
	border-color: #556;
}
table.data td {
	border-color: #556;
}
table.data td.less {
    color: var(--color8-dark1);
}
table.data th img {
	-webkit-filter: hue-rotate(335deg) brightness(90%);
	filter: hue-rotate(335deg) brightness(90%);
}
table.data td img[title="Passworded Rar Archive"] {
	-webkit-filter: invert(40%);
	filter: invert(40%);
}
table tr {
	background-color: var(--bg0-dark4);
}
table tr.alt {
	background-color: var(--bg0-dark2);
}
fieldset {
	border: 1px solid var(--border8-dark4);
}
legend {
	border: 1px solid var(--border8-dark4);
}
div.pager a {
	color: var(--color8-dark3);
	background-color: transparent;
	padding-top: 2px;
}
div.pager a:hover {
	color: var(--color-accent);
	text-decoration: none;
}
div.pager .current {
	color: var(--color8-dark1);
	background-color: transparent;
	border: 1px solid var(--border0-light1);
	-webkit-border-radius: 2px;
	border-radius: 2px;
	padding-top: 2px;
}
.rndbtn {
	background-color: var(--bg0-dark4);
	color: var(--color8-normal);
}
table tr .rndbtn {
	background-color: transparent;
}
table tr.alt .rndbtn {
	background-color: transparent;
}
table.highlight tr:hover .rndbtn {
	background-color: transparent;
}
table.highlight tr:hover .rndbtn:hover {
	color: var(--color8-normal);
	background-color: var(--bg0-light0);
}
img.shadow {
	border: none;
}
div.error {
	border: 1px solid var(--border7-normal);
	background-color: #220606;
}
div.gm-warning {
	border: 1px solid var(--border5-normal);
	background-color: #222206;
}
div.gm-success {
	border: 1px solid var(--border4-normal);
	background-color: #062214;
}
div.gm-info {
	border: 1px solid var(--border3-normal);
	background-color: #061422;
}
.global-message {
	color: var(--color8-normal);
}
.global-message a {
	color: var(--color8-normal) !important;
}
.global-message p, .global-message p>a {
	color: var(--color8-normal);
}
.global-message h2 {
   color: var(--color8-dark3);
}
.sabhistorysuccess {
	background-color: #142206 !important;
}
.sabhistoryfail {
	background-color: #220606 !important;
}
.ui-tooltip-comments .ui-tooltip-titlebar {
	color: #C20063;
	background-color: var(--bg0-dark4);
	border-bottom: none;
}
.ui-tooltip-comments .ui-tooltip-content {
	background-color: var(--bg0-normal);
	border: 3px solid #9B004F;
	border-top: none;
}
div.nzb_multi_operations input {
	color: var(--color8-normal);
	background-color: var(--bg0-dark0);
	border: 1px solid var(--border0-dark0);
	border-radius: 2px;
}
div.nzb_multi_operations input:hover {
	color: var(--color1-normal);
	background-color: var(--bg0-light0);
	border: 1px solid var(--border0-light1);
	border-radius: 2px;
}
div.icon_cart,
div.icon_cart_clicked {
	-webkit-filter: brightness(115%);
	filter: brightness(115%);
}
div.icon_nzb,
div.icon_nzb_clicked  {
	-webkit-filter: brightness(90%);
	filter: brightness(90%);
}
div.icon_sab,
div.icon_sab_clicked {
	-webkit-filter: brightness(90%);
	filter: brightness(90%);
}
div.icon_nzbvortex,
div.icon_nzbvortex_clicked {
	-webkit-filter: brightness(90%);
	filter: brightness(90%);
}
div.icon_nfo {
	-webkit-filter: invert(100%);
	filter: invert(100%);
}
ul.tabs {
	border-bottom: 5px solid var(--border0-dark4);
}
ul.tabs li {
   background: var(--bg0-dark0);
}
ul.tabs li a {
	color: var(--color6-normal);
}
ul.tabs li a:hover {
	background: var(--bg0-light0);
}
html ul.tabs li.active, html ul.tabs li.active a:hover {
	background: var(--bg0-dark4);
	border-bottom: 5px solid var(--border0-dark4);
}
.tab_content {
	background-color: var(--bg0-dark4);
}
.tab_staff {
	background-color: #280B44 !important;
}
.tab_admin {
	background-color: #0B2844 !important;
}
#nav {
	-webkit-border-radius: 2px;
	border-radius: 2px;
	-webkit-box-shadow: none;
	box-shadow: none;
	background: var(--bg0-dark3);
	border-color: var(--border0-dark3);
}
#nav a {
	color: var(--color8-normal);
	-webkit-border-radius: 2px;
	border-radius: 2px;
}
#nav .current a, #nav li:hover>a {
	background: var(--bg0-light0);
	color: var(--color1-normal);
}
#nav ul a:hover {
	background: var(--bg0-light0) !important;
	color: var(--color1-normal) !important;
}
#nav ul {
	color: var(--color8-normal);
	background: var(--bg0-dark3);
	border: solid 1px var(--border0-light1);
	-webkit-border-radius: 2px;
	border-radius: 2px;
}
#nav ul li:first-child > a {
	-webkit-border-top-left-radius: 2px;
	border-top-left-radius: 2px;
	-webkit-border-top-right-radius: 2px;
	border-top-right-radius: 2px;
}
#nav ul li:last-child > a {
	-webkit-border-bottom-left-radius: 2px;
	border-bottom-left-radius: 2px;
	-webkit-border-bottom-right-radius: 2px;
	border-bottom-right-radius: 2px;
}
.menu-donate, .NZBUnityDownloadAll {
	color: var(--color8-normal) !important;
	background-color: var(--bg1-dark2) !important;
	border: none !important;
	border-radius: 2px !important;
}
.menu-donate:hover, .NZBUnityDownloadAll:hover {
	color: var(--color1-normal) !important;
	background-color: var(--bg0-light0) !important;
}
.menu-donate:active, .NZBUnityDownloadAll:active {
	color: var(--color1-dark1) !important;
	background-color: var(--bg0-normal) !important;
}
#headermenu {
	-webkit-border-radius: 2px;
	border-radius: 2px;
	-webkit-box-shadow: none;
	box-shadow: none;
	background: var(--bg0-dark3);
	border: solid 1px var(--border0-dark3);
}
#headermenu a {
	color: var(--color8-normal);
	-webkit-border-radius: 2px;
	border-radius: 2px;
}
#headermenu .current a, #headermenu li:hover>a {
	background: var(--bg0-light0);
	color: var(--color-accent);
}
#headermenu ul a:hover {
	background: var(--bg0-light0) !important;
	color: var(--color1-normal) !important;
}
#headermenu ul {
	color: var(--color8-normal);
	background: var(--bg0-dark3);
	border: solid 1px var(--border0-light1);
	-webkit-border-radius: 2px;
	border-radius: 2px;
}
#headermenu ul li:first-child > a {
	-webkit-border-top-left-radius: 2px;
	border-top-left-radius: 2px;
	-webkit-border-top-right-radius: 2px;
	border-top-right-radius: 2px;
}
#headermenu ul li:last-child > a {
	-webkit-border-bottom-left-radius: 2px;
	border-bottom-left-radius: 2px;
	-webkit-border-bottom-right-radius: 2px;
	border-bottom-right-radius: 2px;
}
#headcat {
	height: 26px;
}
.nukeright, .nukedright, .modnukeright {
	opacity: .7;
}
.unnukeright {
	opacity: .7;
}
.nuke_nzbsright {
	opacity: .7;
}
.badger-outter {
	background: var(--bg0-dark3);
	border: 1px solid var(--border0-dark3);
	box-shadow: none;
	-webkit-box-shadow: none;
}
.badger-inner {
	background: var(--bg7-normal);
}
.badger-badge {
	color: var(--color0-dark3);
	text-shadow: none;
}
.badger-number {
	top: 5px;
}
.meter {
	background: var(--bg0-dark2);
	-webkit-box-shadow: none;
	box-shadow: none;
}
.meter>span {
	opacity: .7;
	background-color: #090;
	-webkit-box-shadow: none;
	box-shadow: none;
}
.orange>span {
	background-color: var(--color6-normal);
}
.red>span {
	background-color: #900;
	background-image: none;
}

