style.css:
:root{
}
div[class^='home_container'] {
  border-radius:0;
}
div[class^='home_sidebar__'] {
  background-color:#3A3A3A;
  padding:0;
  display:grid;
  grid-template-columns:48px 1fr;;
  grid-template-rows:48px 1fr 48px 48px 48px;
}
div[class^='home_sidebar-drag'] {
  display:none;
}
div[class^='home_sidebar-header-bar'] {
  grid-column-start: 1;
  grid-column-end: 1;
  grid-row-start: 2;
  grid-row-end: 2;
  flex-direction:column;
  background-color:#2F2F2F;
}
div[class^='home_sidebar-header'] {
  padding:0;
  background-color:#2F2F2F;
  margin-bottom:0 !important;
}
div[class^='home_sidebar-title'] {
  display:none;
}
div[class^='home_sidebar-sub-title'] {
  display:none;
}
div[class^='home_sidebar-logo'] {
  transform:scale(0.6) translate(-6px,0);
  bottom:0;
}
div[class^='home_sidebar-header-bar']>button[class^='button_icon-button']{
  flex-grow:0 !important;
}
div[class^='home_sidebar__'] button[class^='button_icon-button']{
  width:48px;
  height:48px;
  border-radius:0;
  background:none;
  color:transparent;
}
div[class^='home_sidebar__'] div[class^='button_icon-button-icon'] svg{
  transform:scale(1.5);
  display:block;
  filter:brightness(2);
}
div[class^='home_sidebar__'] div[class^='button_icon-button-text']{
  display:none;
}
div[class^='home_sidebar-body'] {
  background-color:#3A3A3A;
  grid-column-start: 2;
  grid-column-end: 2;
  grid-row-start: 2;
  grid-row-end: 6;
}
div[class^='home_sidebar-tail'] {
  grid-column-start: 1;
  grid-column-end: 1;
  grid-row-start: 3;
  grid-row-end: 6;
  flex-direction:column;
  padding:0;
  background-color:#2F2F2F;
}
div[class^='home_sidebar-actions'] {
  flex-direction:column;
}
div[class^='home_sidebar__']::before{
  content:'搜索联系人';
  display:block;
  background-color:#FFFFFF40;
  background-image:url('data:image/svg+xml,<svg viewBox="0 0 16 16" fill="none" xmlns="http://www.w3.org/2000/svg"><path d="M15.25 15.25 11.5 11.5M.75 7a6.25 6.25 0 1 1 12.5 0A6.25 6.25 0 0 1 .75 7Z" stroke="white" stroke-width="1.5" stroke-linecap="round" stroke-linejoin="round"></path></svg>');
  background-size:16px 16px;
  background-position: 12px center;
  background-repeat:no-repeat;
  grid-column-start: 2;
  grid-column-end: 2;
  grid-row-start: 1;
  grid-row-end: 1;
  color:#FFF;
  font-size:14px;
  padding:6px 36px;
  margin:8px;
  border-radius:4px;
  opacity:.5;
}
div[class^='home_chat-item__'] {
  margin:0;
  padding-left:64px;
  border:none;
  border-radius:0;
  background:none;
  position:relative;
}
div[class*='home_chat-item-selected__'] {
  background-color:#505050;
}
div[class^='home_chat-item__']:hover {
  background-color:#6A6A6A;
}
div[class^='home_chat-item__']::before {
  content:'';
  position:absolute;
  display:block;
  width:46px;
  height:46px;
  left:10px;
  top:10px;
  border-radius:2px;
  background-color:#FFF;
  background-position:center;
  background-size:100% 100%;
  background-repeat:no-repeat;
  background-attachment:cover;
}
div[class^='home_chat-item__']:nth-child(6n+1)::before {
  background-image:url('https://cataas.com/cat?type=small&a=1');
}
div[class^='home_chat-item__']:nth-child(6n+2)::before {
  background-image:url('https://cataas.com/cat?type=small&a=2');
}
div[class^='home_chat-item__']:nth-child(6n+3)::before {
  background-image:url('https://cataas.com/cat?type=small&a=3');
}
div[class^='home_chat-item__']:nth-child(6n+4)::before {
  background-image:url('https://cataas.com/cat?type=small&a=4');
}
div[class^='home_chat-item__']:nth-child(6n+5)::before {
  background-image:url('https://cataas.com/cat?type=small&a=5');
}
div[class^='home_chat-item__']:nth-child(6n+6)::before {
  background-image:url('https://cataas.com/cat?type=small&a=6');
}
div[class^='home_chat-item-title'] {
  color:#FFF;
  line-height:1.5;
  font-size:14px;
}
div[class^='home_chat-item-date'] {
  max-width: 72px;
}
div[class='window-header'] {
  justify-content:center;
  position:relative;
  padding:12px 8px;
  border:none;
}
div[class^='window-header-title'] {
  text-align:center;
}
div[class^='window-header-main-title'] {
  font-size:16px !important;
}
div[class^='window-header-sub-title'] {
  font-size:12px !important;
}
div[class='window-actions'] {
  position:absolute;
  right:8px;
}
div[class='window-actions'] button[class^='button_icon-button']{
  background:none;
  border-color:transparent;
}
div[class^='chat_chat__'] {
  background-color:#F2F2F2;
}
div[class^='chat_chat-message-container'] {
  position:relative;
  padding-left:48px;
}
div[class^='chat_chat-message-header'] {
  position:absolute;
  left:0;
  top:0;
  margin-top:16px;
}
div[class^='chat_chat-message-avatar'] {
  transform:scale(1.25);
}
div[class^='chat_chat-message-actions'] {
  margin-top:-76px !important;
  margin-left:16px !important;
}
div[class^='chat_chat-message-avatar'] button[class^='button_icon-button']{
  
}
div[class^='chat_chat-message__'] div[class^='chat_chat-message-item']{
  background-color:#FFFFFF !important;
  border-radius: 6px !important;
}
div[class^='chat_chat-message-user__'] div[class^='chat_chat-message-item']{
  background-color:#B4EB7C !important;
  border-radius: 6px !important;
}
div[class^='chat_chat-message-user__'] div[class^='chat_chat-message-container'] {
  position:relative;
  padding-right:48px;
}
div[class^='chat_chat-message-user__'] div[class^='chat_chat-message-header'] {
  position:absolute;
  left:auto;
  right:0;
  top:0;
  margin-top:16px;
}
div[class^='chat_chat-input-panel'] {
  background:#FFF;
}
div[class^='chat_chat-input-action'] {
  border-color:transparent !important;
  box-shadow:none !important;
}
label[class^='chat_chat-input-panel-inner'] {
  border-radius:4px;
}
label[class^='chat_chat-input-panel-inner'] button[class^='button_icon-button'] {
  border-radius:4px;
  background:#212121;
  filter:invert(1);
  opacity:0.6;
}
