style.css:
/* i really want this to be global */

.content {
    overflow: visible !important;
    display: block !important;
    max-height: none !important;
    height: auto !important;
}

.innerContentContainer b, .innerContentContainer strong {
  color: hsl(4, 73%, 56%);
}

.innerContentContainer i, .innerContentContainer em {
  color: hsl(205, 67%, 47%);
}

.innerContentContainer b i, .innerContentContainer i b {
  color: hsl(309, 15%, 46%);
}

.done {
	color: seagreen;
}
