style.css:
@-moz-document domain("shopee.ph"), regexp("https?://(www\.)?shopee\.[a-z]{2,3}(\.[a-z]{2,3})?(/.*)?") {
:root {
    scrollbar-color: var(--black) var(--white) !important;
    filter: invert(0.9) brightness(105%) contrast(1.1);
    --shopee: #a9e41a;
    --shopee2: #17cd3e;
    --shopee3: #f70;
    --white: #000;
    --white2: #828181;
    --black: #fff;
    --brand-primary-color: #e53c1b;
  }
  h1,
  h2,
  h3,
  h4,
  h5,
  h6,
  p {
    color: var(--white);
  }
  .shopee-top {
    background: var(--shopee);
    background: linear-gradient(-180deg, var(--shopee), var(--shopee2));
    transition: transform 0.2s cubic-bezier(0.4, 0, 0.2, 1);
  }
  .icon-shopee-logo {
    fill: var(--black);
  }
  [style^="--nc-primary"] [class$="nt-regular nt-normal"],
  [aria-label="buttonClaim"],
  .navbar-wrapper--without-search,
  .shopee-button-solid--primary {
    background: var(--shopee) !important;
    color: var(--white);
  }
  [style^="--nc-primary"] [class$="nt-regular nt-normal"]:hover,
  [aria-label="buttonClaim"]:hover,
  .navbar-wrapper--without-search:hover,
  .shopee-button-solid--primary:hover {
    background: var(--shopee2) !important;
  }
  button.shopee-sort-by-options__option.shopee-sort-by-options__option--selected,
  .btn-solid-primary:not(.btn-solid-primary--disabled),
  .header-with-search-wrapper .shopee-searchbar>.btn-solid-primary:not(.disabled) {
    background: var(--shopee) !important;
    color: var(--black);
  }
  button.shopee-sort-by-options__option.shopee-sort-by-options__option--selected:hover,
  .btn-solid-primary:not(.btn-solid-primary--disabled):hover,
  .header-with-search-wrapper .shopee-searchbar>.btn-solid-primary:not(.disabled):hover {
    background: var(--shopee2) !important;
  }
  .shopee_ic .bg-shopee-voucher-yellow {
    background-color: var(--shopee2);
  }
  [style^="background-color: rgb(208"] {
    background-color: var(--background) !important;
  }
  [role="contentinfo"],
  .shopee-cart-number-badge,
  .shopee-notification-number-badge,
  .cart-page-logo__page-name {
    border-color: var(--shopee);
    color: var(--shopee);
  }
  .shopee_ic [class$="hover:border-shopee-primary"]:hover,
  .shopee_ic .bg-shopee-primary {
    background-color: var(--shopee2);
    border-color: var(--shopee);
  }
  .shopee-cart-number-badge,
  .shopee-notification-number-badge {
    background: var(--white);
  }
  .cart-page-logo .icon-shopee-logo {
    fill: var(--shopee);
  }
  .carousel-arrow--hint > .shopee-svg-icon {
    fill: var(--shopee3);
  }
  .carousel-arrow--hint > .shopee-svg-icon:hover {
    fill: var(--shopee);
  }
  .home-category-list__category-grid {
    background: var(--black) !important;
    color: #fff5f5 !important;
  }
  [class="header-with-search__search-section"] [href^="/"],
  button.shopee-sort-by-options__option.shopee-sort-by-options__option--selected,
  .btn-solid-primary:not(.btn-solid-primary--disabled),
  .navbar-wrapper .navbar__link-icon,
  .navbar-wrapper .navbar__link-text,
  .header-with-search__search-section [aria-hidden="false"],
  [class^="flex v-center"] a,
  [class^="flex v-center"] .flex,
  .shopee-drawer #temporaryId,
  .shopee_ic .text-white,
  .home-category-list__category-grid [class*="_"] {
    color: var(--white) !important;
    font-weight: 500;
  }
  [class^="flex-auto flex-column"] .flex button:first-of-type div {
    border-bottom-color: var(--shopee);
    color: var(--shopee);
  }
  [href^="https://shopee.ph/search?brands"],
  [class^="flex items-center"] [href^="/"],
  [class$="page-product__breadcrumb"] [href^="/"],
  .shopee_ic .font-medium,
  .v-center [class="pxp__e v-center"] div,
  [class^="text-"],
  [class^="flex items-center"] [class*="flex items-center"] div,
  .shopee-countdown-timer,
  .stardust-tabs-header__tab--active div,
  .jjcGRu.C_18Pl,
  .homepage-mall-section .ofs-carousel__item__text {
    color: var(--shopee);
  }
  .VAir_o,
  [class^="flex-auto flex-column"] [style^="width"] div {
    filter: invert(1);
  }
  .stardust-tabs-header__tab--active [class*=" "] {
    background-color: var(--shopee);
  }
  .btn-solid-primary--link,
  .stardust-popover__target span,
  .navbar__username,
  .header-with-search__search-section [class*="_"] {
    color: var(--white);
  }
  [href^="/"] [style^="width: 100px"] [class*=" "][class^="_"] {
    color: var(--white2);
  }
  .cart-drawer .icon-shopping-cart-2 {
    stroke: var(--black);
  }
  .BCpWjM,
  .nEvdHo.l3xkHz,
  shopee-vertical-stack-ui,
  [style^="background-color"] [style*="file"],
  [class$="header-navbar-background header-navbar-instagram-png"],
  [class$="header-navbar-background header-navbar-facebook-png"],
  [class^="flex-auto flex-column"] [style^="width: 100%"] div,
  [aria-label^="title"],
  .full-home-banners-wrapper [class^="_"] [href^="/"],
  .home-page .container [data-current-src^="https:"] [href^="/shop/"],
  .container [style*="background-image"],
  [baseurl^="https://shopee."] {
    filter: invert(1) !important;
  }
  [class="shopee-floating-icons__wrapper"],
  shopee-carousel-ui,
  shopee-banner-popup-stateful {
    filter: invert(1);
    z-index: 9999;
  }
  video,
  img,
  svg,
	.poster__image,
	.content__country,
  [class$="items-center"] [class^="sprite-product-sharing sprite-product-sharing-fm"] {
    filter: invert(1) !important;
}
}
