style.css:
/* Apply a global style to the body */
body {
  /* Set the background to a linear gradient from light red to dark red */
  background: linear-gradient(to bottom, #ffcccc, #cc0000);
  /* Set the font color to a contrasting color */
  color: #ffffff;
  /* Add a bit of padding to the body for better spacing */
  padding: 20px;
  /* Use a nice font */
  font-family: Arial, sans-serif;
}

/* Style headings with a deep red color and a shadow effect */
h1, h2, h3, h4, h5, h6 {
  color: #cc0000; /* Deep red color */
  text-shadow: 2px 2px 4px rgba(0, 0, 0, 0.5); /* Shadow effect */
}

/* Style paragraphs with a lighter shadow and a bit of margin for spacing */
p {
  color: #f2f2f2;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.3);
  margin-bottom: 20px;
}

/* Add a shadow to all block elements like divs */
div {
  /* Box shadow for a subtle depth effect */
  box-shadow: 0px 4px 8px rgba(0, 0, 0, 0.5);
  /* Add some padding inside the div for better content spacing */
  padding: 20px;
  /* Round the corners a bit */
  border-radius: 8px;
  /* Ensure divs don't overflow */
  overflow: hidden;
}

/* Style links with a gradient background, color, and shadow */
a {
  color: #ffffff;
  text-shadow: 1px 1px 2px rgba(0, 0, 0, 0.5);
  background: linear-gradient(to right, #ff4d4d, #cc0000);
  padding: 10px;
  text-decoration: none;
  border-radius: 4px;
  /* Add a shadow to the link */
  box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.3);
}

/* Add a hover effect to links */
a:hover {
  background: linear-gradient(to right, #ff6666, #990000);
}
