style.css:
@-moz-document domain("whatsapp.com") {
._amk6, .xr5525 {
    /* All Messages and Message Flicks (grouped), and timestamps (xr5525) */
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
    background-color: rgba(70, 80, 90, 0.2) !important;
    backdrop-filter: blur(1.5px);
    outline: 2px solid rgba(70, 80, 90, 0.5);
    margin-top: 8px;
  }

  ._amk7 {
    /* All Message Flicks */
    color: rgba(70, 80, 90, 0.2) !important;
  }

  .x1k6n2fr, ._aju3 {
    /* All Message Embeds */
    background-color: rgba(70, 80, 90, 0.3) !important;
  }

  /* --- --- --- */

  ._akbw {
    /* This message was deleted */
    font-style: normal;
  }

  /* --- --- --- */

  /* Reactions */
  .x8bomo0 { border-bottom-color: rgba(0, 0, 0, 0); }
  .x1qco5ih { border-top-color: rgba(0, 0, 0, 0); }
  .xiulc8h { border-left-color: rgba(0, 0, 0, 0); }
  .x1qco5ih { border-right-color: rgba(0, 0, 0, 0); }
  .x1gslqiq {
    background-color: rgba(70, 80, 90, 0.2);
    backdrop-filter: blur(1.5px);
    outline: 2px solid rgba(70, 80, 90, 0.5);
  }
}
