style.css:
@-moz-document domain("youtube.com") {
/* Apply Georgia font and #7B3F00 color to most text */
  body, 
  ytd-app, 
  yt-formatted-string, 
  yt-formatted-string *, 
  ytd-rich-item-renderer,
  ytd-video-meta-block,
  ytd-channel-name,
  ytd-video-secondary-info-renderer,
  ytd-comments,
  ytd-comment-renderer,
  #video-title,
  .ytd-video-primary-info-renderer,
  .ytd-watch-metadata {
    font-family: Georgia, serif !important;
    color: #7B3F00 !important;
  }

  /* Change background color to light pink */
  body,
  #page-manager,
  ytd-app {
    background-color: #FFF0F3 !important;
  }

  /* Change border color to #FFE2E6 */
  *:not(body):not(html):not(ytd-player) {
    border-color: #FFE2E6 !important;
  }

  /* Style for the options box (three dots menu) */
  ytd-menu-popup-renderer {
    background-color: #FFF0F3 !important;
  }

  /* Remove pink background from video controls */
  .ytp-chrome-controls .ytp-button {
    background-color: transparent !important;
  }

  /* Remove black box from "All" in topics */
  yt-chip-cloud-chip-renderer[selected] {
    background-color: transparent !important;
  }

  /* Ensure buttons and input fields are visible */
  button:not(.ytp-button),
  input {
    background-color: #FFE2E6 !important;
    color: #7B3F00 !important;
  }

  /* Ensure video player is not affected */
  ytd-player {
    all: revert !important;
  }

  /* Remove brown box in left bottom corner of video */
  .ytp-ce-element {
    display: none !important;
  }
}
