style.css:
@-moz-document domain("zoom.us"), domain("teams.microsoft.com"), domain("meet.google.com") {
.chat-window {
        user-select: text; /* Allow text selection */
        -webkit-user-select: text;
        -moz-user-select: text;
        -ms-user-select: text;
    }

    .chat-window::selection {
        background: transparent; /* Transparent background for selected text */
        color: inherit; /* Ensure the text color is unchanged */
    }
}
