/* CSS untuk mengatur warna progress bar di Metabase */

/* Mengatur warna background progress bar secara keseluruhan */
div[data-testid="progress-bar"] {
    background-color: #FF6F61 !important; /* Warna merah muda */
    border-radius: 5px !important; /* Menjaga border-radius */
    overflow: hidden !important; /* Menjaga overflow */
    height: 28px !important; /* Menjaga tinggi elemen */
}

/* Mengatur warna bagian yang sudah terisi berdasarkan nilai */
div[data-testid="progress-bar"] > div {
    background: linear-gradient(
        to right,
        #FF6F61 0%,   /* Merah muda, 0% */
        #FF6F61 50%,  /* Merah muda, 50% */
        #FDD835 50%,  /* Kuning, 50% */
        #FDD835 70%,  /* Kuning, 70% */
        #FFB300 70%,  /* Oranye, 70% */
        #FFB300 80%,  /* Oranye, 80% */
        #8BC34A 80%,  /* Hijau terang, 80% */
        #8BC34A 90%,  /* Hijau terang, 90% */
        #4CAF50 90%,  /* Hijau tua, 90% */
        #4CAF50 100%  /* Hijau tua, 100% */
    ) !important;
    width: 100% !important; /* Menjaga lebar elemen */
    height: 100% !important; /* Menjaga tinggi elemen */
}

/* Mengatur warna bagian indikator */
div[data-testid="progress-bar"]::after {
    content: '';
    display: block;
    width: 100%;
    height: 100%;
    background: linear-gradient(
        to right,
        #FF6F61 0%,   /* Merah muda, 0% */
        #FF6F61 50%,  /* Merah muda, 50% */
        #FDD835 50%,  /* Kuning, 50% */
        #FDD835 70%,  /* Kuning, 70% */
        #FFB300 70%,  /* Oranye, 70% */
        #FFB300 80%,  /* Oranye, 80% */
        #8BC34A 80%,  /* Hijau terang, 80% */
        #8BC34A 90%,  /* Hijau terang, 90% */
        #4CAF50 90%,  /* Hijau tua, 90% */
        #4CAF50 100%  /* Hijau tua, 100% */
    ) !important;
    position: absolute;
    top: 0;
    left: 0;
    z-index: 1;
}
