style.css:
@-moz-document domain("codeforces.com") {
:root {
  --bg-color: #161f31;
  --bg-dark-color: #121928;
  --fg-color: #c9d8ee;
  --fg-dark-color: #a9bad6;
  --red-color: #ff3939;
  --orange-color: #ff9f39;
  --yellow-color: #ffea39;
  --purple-color: #d62ed6;
  --cyan-color: #42caca;
  --green-color: #64f923;
  --blue-color: #5889ff;
  --pink-color: #f36cde;
  --border-color: #e1e1e1;
  --none: none;
}

div,
dl,
dt,
dd,
ul,
ol,
li,
pre,
fieldset,
input,
p,
blockquote,
th,
td,
i,
i::before,
font,
body {
  color: var(--fg-color) !important;
}

h1,
h2,
h3,
h4,
h5,
h6 {
  color: white !important;
}

ins {
  color: var(--green-color) !important;
  background: none !important;
}

del {
  color: var(--red-color) !important;
  background: none !important;
}

a {
  color: var(--pink-color);
}

a:link {
  color: var(--blue-color);
}

a:hover {
  filter: brightness(0.8);
  background: none !important;
}

body {
  background: var(--bg-color);
}

table {
  border-collapse: collapse !important;
}

div {
  background: inherit !important;
}

table,
noscript,
select,
input,
tr,
td,
th,
option,
dpPopup1,
.option,
.opt {
  border: 1px solid var(--border-color) !important;
  background: var(--bg-dark-color) !important;
  color: var(--fg-color) !important;
}

.datatable {
  background: none !important;
}

.contests-table,
.roundbox {
  background: var(--bg-dark-color) !important;
  color: var(--fg-color);
}

.roundbox .dark {
  color: #ccc !important;
  background: #2c2c2c !important;
}

.roundbox .bottom-links {
  background: #2c2c2c !important;
}
.roundbox .roundbox-rt,
.roundbox .roundbox-lt,
.roundbox .roundbox-lb {
  background: none;
}

._CatalogHistorySidebarFrame_item {
  overflow-wrap: break-word;
}

._CatalogViewFrame_choose-time {
  background: var(--bg-dark-color) !important;
}

._CatalogViewFrame_choose-time ul li:hover {
  filter: brightness(0.5);
  color: var(--pink-color) !important;
}

#vote-list-filterdifficultylowerborder,
.vote-item {
  background: white !important;
}

.description-block {
  background: #2c2c2c !important;
  color: #ccc !important;
  text-decoration: #ccc;
}

.mathjax_preview,
.math {
  color: white !important;
}

.graphtype {
  margin-bottom: 10px;
}

span.legendary-user-first-letter {
  color: white !important;
}

div.new-post a {
  background: #2c2c2c !important;
  color: #ccc !important;
  text-decoration: #ccc;
}

.menu-list li a {
  color: var(--fg-color);
}

element.style {
  color: var(--orange-color);
  background: var(--orange-color);
}

.second-level-menu-list li a {
  color: var(--cyan-color);
}

.second-level-menu-list li.backlava .leftlava {
  background: black;
}

.second-level-menu-list li.backlava .cornerlava {
  background: black;
}

.second-level-menu-list li.backlava {
  background: var(--bg-color) !important;
  border-radius: 25px !important;
}

.second-level-menu-list li.current.selectedLava {
  background: var(--bg-color) !important;
}

.search {
  background: var(--bg-dark-color) !important;
}

.user-admin {
  color: var(--cyan-color) !important;
}

.user-black {
  color: var(--red-color) !important;
}

.topic .content {
  border-left: 4px solid #505050 !important;
}

.problem-statement .sample-tests pre {
  background: var(--bg-dark-color) !important;
}

#facebox .content,
#facebox .popup,
#facebox .close {
  background: var(--bg-dark-color) !important;
}

.confirm-proto .text {
  background: var(--bg-dark-color) !important;
}

.test-for-popup pre {
  background: #404040 !important;
}
.pln {
  color: #ccc !important;
}
.kwd {
  color: #326ddc !important;
}
.str {
  color: #2ab92a !important;
}
.com {
  color: #dc4154 !important;
}
.pun,
.opn,
.clo {
  color: #ffb01f !important;
}

#tooltip {
  background: #202020 !important;
  opacity: 0.95 !important;
}

.sidebar-menu ul a {
  color: var(--fg-color) !important;
}

.sidebar-menu ul li {
  background: #404040 !important;
}

body > div.button-up {
  background: var(--bg-dark-color) !important;
}

#pagecontent > div.second-level-menu > ul > li.backlava > div.leftlava {
  border-radius: 1px !important;
  border-color: red;
}

div.ttypography .tt {
  background: none !important;
  color: var(--cyan-color);
  border: none;
}

.file .text {
  background: #444;
}

.lit {
  color: var(--cyan-color);
}

.verdict-rejected {
  color: var(--blue-color);
}

.typ {
  color: var(--purple-color);
}

.handleForm div {
  text-align: left !important;
}

._children,
._children li {
  padding-left: 15px !important;
}
._nameBody {
  max-width: calc(100% - 3rem);
}
}
