style.css:
@-moz-document domain("play.google.com") {
/* Body and General */
    body, .CwaK9 {
        background-color: #0d0d0d !important; /* Fundo preto */
        color: #e0e0e0 !important; /* Texto cinza claro */
        font-family: "Lucida Console", Monaco, monospace !important; /* Fonte monospaced */
    }

    /* Header */
    header, .gb_hb {
        background-color: #1f1f1f !important; /* Preto escuro */
        border-bottom: 2px solid #551a8b !important; /* Linha inferior roxa escura */
        box-shadow: 0 4px 8px rgba(85, 26, 139, 0.5) !important; /* Sombra roxa */
    }

    /* Search bar */
    .gb_qf, .gb_oe, .gb_Pc, .gb_Oe {
        background-color: #333333 !important; /* Cinza médio */
        border: 1px solid #551a8b !important; /* Bordas roxas escuras */
        color: #e0e0e0 !important; /* Texto cinza claro */
        border-radius: 5px !important; /* Bordas arredondadas */
        padding: 5px !important; /* Espaçamento interno */
    }

    /* Text Inputs */
    input, textarea {
        background-color: #2b2b2b !important;
        color: #e0e0e0 !important;
        border: 1px solid #551a8b !important;
        border-radius: 5px !important;
        padding: 5px !important;
    }

    /* Buttons */
    button, .btn {
        background-color: #551a8b !important; /* Fundo roxo escuro */
        color: #ffffff !important; /* Texto branco */
        border: 1px solid #1f1f1f !important; /* Bordas preto escuro */
        border-radius: 5px !important;
        padding: 10px 15px !important;
        box-shadow: 0 4px 6px rgba(85, 26, 139, 0.5) !important; /* Sombra roxa */
        transition: background-color 0.3s, box-shadow 0.3s !important;
    }
    
    button:hover, .btn:hover {
        background-color: #6a1f9e !important; /* Fundo roxo mais claro */
        box-shadow: 0 6px 8px rgba(85, 26, 139, 0.7) !important; /* Sombra mais intensa */
    }

    /* Links */
    a {
        color: #8b0000 !important; /* Vermelho escuro */
        text-decoration: none !important; /* Sem sublinhado */
        transition: color 0.3s !important;
    }

    a:hover {
        color: #ff4500 !important; /* Laranja */
        text-decoration: underline !important; /* Sublinhado ao passar o mouse */
    }

    /* Cards */
    .card, .WHE7ib, .T75of {
        background-color: #1f1f1f !important; /* Fundo preto escuro */
        border: 1px solid #551a8b !important; /* Bordas roxas escuras */
        border-radius: 10px !important;
        box-shadow: 0 4px 8px rgba(85, 26, 139, 0.5) !important; /* Sombra roxa */
        padding: 10px !important;
        transition: transform 0.3s, box-shadow 0.3s !important;
    }
    
    .card:hover, .WHE7ib:hover, .T75of:hover {
        transform: translateY(-5px) !important; /* Efeito de levantar ao passar o mouse */
        box-shadow: 0 8px 12px rgba(85, 26, 139, 0.7) !important; /* Sombra mais intensa */
    }

    /* Titles */
    .WsMG1c, .b8cIId, .AHFaub {
        color: #8b0000 !important; /* Títulos vermelhos escuros */
        font-family: "Lucida Blackletter", "Old English Text MT", serif !important; /* Fonte gótica */
        text-shadow: 1px 1px 2px #551a8b !important; /* Sombra de texto roxa */
    }

    /* Sidebar */
    .LkLjZd.ScJHi.OzU4dc, .lOxH2d {
        background-color: #0d0d0d !important;
        border-right: 1px solid #551a8b !important; /* Bordas roxas escuras */
    }

    /* Footer */
    .KojYOc, .ZilZHe {
        background-color: #0d0d0d !important;
        color: #e0e0e0 !important;
        border-top: 2px solid #551a8b !important; /* Linha superior roxa escura */
        padding: 10px !important;
    }

    /* Icons */
    .gb_Me, .gb_Le, .gb_Kd {
        color: #8b0000 !important; /* Ícones vermelhos escuros */
        transition: color 0.3s !important;
    }

    .gb_Me:hover, .gb_Le:hover, .gb_Kd:hover {
        color: #ff4500 !important; /* Ícones laranja ao passar o mouse */
    }

    /* Shadows */
    .VfPpkd-LgbsSe-OWXEXe-k8QpJ {
        box-shadow: 0 4px 8px rgba(85, 26, 139, 0.5) !important; /* Sombra roxa */
    }

    /* Misc */
    .gb_oc, .gb_Sd {
        color: #e0e0e0 !important;
    }

    /* Modal backgrounds */
    .GAcYo, .oJeWuf {
        background-color: #2b2b2b !important;
        border: 1px solid #551a8b !important;
        border-radius: 10px !important;
        padding: 15px !important;
        box-shadow: 0 4px 8px rgba(85, 26, 139, 0.5) !important; /* Sombra roxa */
    }

    /* List items */
    .T4LgNb, .u60jwe {
        background-color: #1f1f1f !important;
        border-bottom: 1px solid #551a8b !important;
        padding: 10px !important;
        transition: background-color 0.3s !important;
    }

    .T4LgNb:hover, .u60jwe:hover {
        background-color: #2b2b2b !important; /* Fundo cinza mais claro ao passar o mouse */
    }

    /* Dropdown menus */
    .VfPpkd-StrnGf-rymPhb-ibnC6b {
        background-color: #1f1f1f !important;
        border: 1px solid #551a8b !important;
        color: #e0e0e0 !important;
        border-radius: 5px !important;
    }

    .VfPpkd-StrnGf-rymPhb-ibnC6b:hover {
        background-color: #2b2b2b !important;
    }

    /* Tooltips */
    .goog-tooltip {
        background-color: #551a8b !important;
        color: #ffffff !important;
        border-radius: 5px !important;
        padding: 5px !important;
        box-shadow: 0 4px 6px rgba(85, 26, 139, 0.5) !important; /* Sombra roxa */
    }

    /* Scrollbar */
    ::-webkit-scrollbar {
        width: 12px !important;
        background-color: #1f1f1f !important;
    }

    ::-webkit-scrollbar-thumb {
        background-color: #551a8b !important;
        border-radius: 10px !important;
    }

    /* Notifications */
    .VfPpkd-muHVFf-bMcfAe {
        background-color: #1f1f1f !important;
        border: 1px solid #551a8b !important;
        color: #e0e0e0 !important;
        border-radius: 5px !important;
        padding: 10px !important;
        box-shadow: 0 4px 6px rgba(85, 26, 139, 0.5) !important; /* Sombra roxa */
    }
}
