style.css:
@-moz-document domain("viblo.asia") {
:root {
    --bg-color: #161f31;
    --bg-dark-color: #121928;
    --fg-color: #c9d8ee;
    --fg-dark-color: #a9bad6;
    --red-color: #ff3939;
    --orange-color: #ff9f39;
    --yellow-color: #ffea39;
    --purple-color: #d62ed6;
    --cyan-color: #42caca;
    --green-color: #64f923;
    --blue-color: #5889ff;
    --pink-color: #f36cde;
    --border-color: #e1e1e1;
    --none: none;
  }

  div,
  dl,
  dt,
  dd,
  ul,
  ol,
  li,
  pre,
  label,
  fieldset,
  input,
  p,
  blockquote,
  th,
  td,
  i,
  i::before,
  font,
  body {
    color: var(--fg-color) !important;
  }

  main,
  body {
    background: var(--bg-color);
  }

  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    color: white !important;
  }

  ins {
    color: var(--green-color) !important;
    background: none !important;
  }

  del {
    color: var(--red-color) !important;
    background: none !important;
  }

  a {
    color: var(--pink-color);
  }

  a:link {
    color: var(--cyan-color);
  }

  a:hover {
    filter: brightness(0.8);
    background: none !important;
  }

  img {
    filter: invert(100%) brightness(95%);
  }

  pre[class^="language"],
  code {
    background: var(--bg-dark-color) !important;
    color: var(--fg-dark-color) !important;
  }

  .image-avt,
  .main-navbar__logo img,
  .avatar {
    filter: none;
  }

  table {
    border-collapse: collapse !important;
  }

  table,
  noscript,
  select,
  input,
  tr,
  td,
  th,
  option {
    border: 1px solid var(--border-color) !important;
    background: var(--bg-dark-color) !important;
    color: var(--fg-color) !important;
  }

  .post-actions__clip,
  .el-button,
  .el-tag {
    background: var(--bg-dark-color) !important;
  }

  .main-navbar {
    background: var(--bg-dark-color) !important;
  }

  .el-dialog__body {
    border: 1px solid var(--border-color) !important;
  }
  .content,
  .card-body {
    border: 1px solid var(--border-color) !important;
    background: var(--bg-dark-color) !important;
  }

  .content {
    padding: 10px;
  }
  .text-register {
    color: var(--fg-dark-color) !important;
  }
  .register-button {
    background: var(--green-color) !important;
  }
  .suggestion-item-content {
    color: var(--fg-dark-color) !important;
  }
  .series-sidebar__post-title {
    color: var(--fg-dark-color) !important;
  }
  .nuxt-link-exact-active > .series-sidebar__post-title {
    color: var(--orange-color) !important;
  }

  .swiper-pagination-bullet {
    background-color: white;
    opacity: 1;
  }

  .v-avatar {
    filter: none;
  }

  .swiper-pagination-bullet.swiper-pagination-bullet-active {
    background-color: var(--pink-color);
  }

  .simple-translate-panel {
    border: 1px solid var(--border-color) !important;
    background: var(--bg-dark-color) !important;
    color: var(--fg-dark-color) !important;
  }

  .el-dialog {
    background: var(--bg-dark-color) !important;
  }
  .el-alert {
    background: var(--bg-dark-color) !important;
  }

  .el-alert--warning,
  .el-alert--warning * {
    color: var(--yellow-color) !important;
  }

  .el-alert-info,
  .el-alert-info * {
    color: var(--blue-color) !important;
  }

  .el-alert-error,
  .el-alert-error * {
    color: var(--red-color) !important;
  }

  .el-alert-success,
  .el-alert-success * {
    color: var(--green-color) !important;
  }

  .v-layout-popup,
  .v-layout-header,
  .v-layout-footer,
  .ant-card-body,
  .ant-card-head,
  .ant-menu-item,
  .ant-dropdown,
  .v-layout-learning-path,
  .v-layout-footer__content .ant-menu-item {
    background: var(--bg-dark-color) !important;
  }
  .ant-menu-item::before {
    background: var(--bg-dark-color) !important;
    color: var(--fg-dark-color) !important;
  }

  .v-menu .ant-menu-item a {
    color: var(--fg-dark-color) !important;
  }

  .tab-active a,
  .v-menu .ant-menu-item.ant-menu-item-selected a {
    color: var(--pink-color) !important;
  }

  .course-detail {
    background: var(--bg-dark-color) !important;
    border: 1px solid var(--border-color) !important;
  }

  .suggestions-slide {
    background: var(--bg-dark-color) !important;
    border: 1px solid var(--border-color) !important;
  }
  .contribute-card {
    background: var(--bg-dark-color) !important;
    border: 1px solid var(--border-color) !important;
  }
  .ant-select-selection {
    background: var(--bg-dark-color) !important;
  }

  .roadmap-item__content {
    background: var(--bg-dark-color) !important;
  }
  .card-top {
    background: var(--bg-dark-color) !important;
    border: 1px solid var(--border-color) !important;
  }

  .v-menu .ant-menu {
    background: var(--bg-dark-color) !important;
  }
  .v-my-questions-table .ant-table-thead > tr > th {
    background: black !important;
  }

  .fixed-bottom-bar {
    background: var(--bg-dark-color) !important;
  }

  .bg-white {
    background: var(--bg-color) !important;
  }
  .cross-bar {
    background: var(--bg-dark-color) !important;
  }
  .certificates__tags {
    background: var(--bg-dark-color) !important;
  }
  .certificates__categories {
    background: var(--bg-dark-color) !important;
  }

  .ant-tag,
  .ant-input,
  .ant-checkbox-inner {
    background: black !important;
  }
  .ant-input-search.ant-input-affix-wrapper input {
    background: black !important;
    border: 1px solid var(--border-color) !important;
  }

  .certificates__item .body {
    background: var(--bg-dark-color) !important;
  }

  .certificates__item .body .title,
  .certificates__item .body .title * {
    color: white !important;
  }

  .certificates__sortby .ant-select-selection {
    background: var(--bg-dark-color) !important;
  }

  .pagination li {
    background: black !important;
  }

  blockquote {
    background: var(--bg-dark-color) !important;
    border: 1px solid var(--border-color) !important;
    color: var(--fg-dark-color) !important;
  }

  .post-bottom-bar {
    background: var(--bg-dark-color) !important;
    color: var(--fg-dark-color) !important;
  }
}
