style.css:
@-moz-document domain("www.coursera.org") {
:root {
    --bg-color: #161f31;
    --bg-dark-color: #121928;
    --fg-color: #c9d8ee;
    --fg-dark-color: #a9bad6;
    --red-color: #ff3939;
    --orange-color: #ff9f39;
    --yellow-color: #ffea39;
    --purple-color: #d62ed6;
    --cyan-color: #42caca;
    --green-color: #64f923;
    --blue-color: #5889ff;
    --pink-color: #f36cde;
    --border-color: #e1e1e1;
    --none: none;
  }

  * {
    box-shadow: none;
  }

  div,
  dl,
  dt,
  dd,
  ul,
  ol,
  li,
  pre,
  label,
  fieldset,
  input,
  p,
  blockquote,
  th,
  td,
  i,
  i::before,
  font,
  body {
    color: var(--fg-color) !important;
  }
  pre[class^="language"],
  code {
    background: var(--bg-dark-color) !important;
    color: var(--fg-dark-color) !important;
  }

  h1,
  h2,
  h3,
  h4,
  h5,
  h6 {
    color: white !important;
  }

  ins {
    color: var(--green-color) !important;
    background: none !important;
  }

  del {
    color: var(--red-color) !important;
    background: none !important;
  }

  a {
    color: var(--pink-color);
    border: none !important;
  }

  a:link {
    color: var(--cyan-color);
  }

  a:hover *,
  a:hover {
    color: var(--orange-color) !important;
  }

  button:hover {
    opacity: 0.8;
  }

  body {
    background: var(--bg-color);
  }

  table {
    border-collapse: collapse !important;
  }

  table,
  noscript,
  select,
  input,
  tr,
  td,
  th,
  option {
    border: 1px solid var(--border-color) !important;
    background: var(--bg-dark-color) !important;
    color: var(--fg-color) !important;
  }

  .mega-menu-overlay div,
  .mega-menu-overlay span,
  .mega-menu-overlay a,
  *:hover div,
  div {
    background: inherit !important;
  }

  .css-1y3sdlb {
    background: var(--bg-color) !important;
  }

  .css-1heqbvx,
  .item .white.css-13o8pm8,
  .drift-widget-message-preview-text,
  .drift-widget-message-preview-text span,
  .timezone-modal-content,
  .css-5zl6nh,
  .css-11tuet4,
  .css-1xftwrc,
  .cds-ProductCard-base {
    border: 1px solid var(--border-color) !important;
    background: var(--bg-dark-color) !important;
