style.css:
@-moz-document domain("fandom.com") {
/*This code hides unimportant things and also makes the Fandom sidebar smaller*/
/*EVERYTHING HAS !important because i dont want to update this everytime fandom updates*/
/*age-questionnare*/
div#age-gate, div.AgeGateDialog-module_modal__huX7o, /*Questionnare fill-out*/ div.marketing-notifications {
  visibility: hidden !important;
}
/*Lower transparency of Fandom's sidebar that you can't remove without mods*/
div.global-navigation {
  opacity: 0.5 !important;
  /*shrinks the sidebar*/
  height: 135px !important;
  /*adds curve so my eyes are pleased*/
  border-bottom-right-radius: 10px !important;
}
/*adds curve to the bottom sidebar so my eyes are pleased*/
div.global-navigation__bottom {
  border-top-right-radius: 10px !important;
}
/*hides unimportant buttons*/
div.global-navigation__links {
  visibility: hidden !important;
  height: 0px !important;
}
/* hide big top advertisement*/
div.top-ads-container {
  visibility: hidden !important;
  height: 0px !important;
  padding: 0px !important;
}
/*Hide more ads*/
div.feed-section__ad {
  visibility: hidden !important;
  height: 0px !important;
  margin: 0px !important;
}
/*hides the background of the global-navigation__top because it interferes with the ui curve*/
div.global-navigation__top {
  background-color: rgba(0,0,0,0) !Important;
}
/*lower transparency of sign in buttons*/
div.global-registration-buttons {
  opacity: 0.5;
}
/*popup/ads removers*/
div#WikiaBarWrapper.WikiaBarWrapper {
  display: none !important;
  visibility: hidden !important;
}
cnx.cnx-content-wrapper {
  visibility: hidden !important;
  display: none !important;
}
cnx.cnx-ratio {
  height: 0px !important;
  width: 0px !important;
}
#incontent_player, .gpd-ad {
  visibility: hidden !important;
  display: none !important;
  height: 0px !important;
  width: 0px !important;
}
.openweb-ad-unit, a[aria-labelledby="ad"], #ad, .ad-slot-placeholder, .incontent-leaderboard, div[itemprop="video"], #rail-boxad-wrapper .gpt-ad, .ad-slot, .is-sponsored {
  display: none !important;
  visibility: hidden !important;
  height: 0px !important;
  width: 0px !important;
}
/*stretch background images to make it fit with the new sidebar*/
.fandom-community-header__background.tileHorizontally.header, .search-modal::before {
  width: 110% !important;
  right: 100px !important;
}
.fl-scrolls-hidden {
  visibility: hidden !important;
}
.wds-dropdown__content {
  opacity: 1 !important;
}
.search-modal::before {
  left: 0px !important;
}
.search-modal {
  right: 10% !important;
}
/*i've ran out of ideas*/
}

@-moz-document domain("www.fandom.com") {
/*This script undoes some changes when on the main fandom.com, where some stuff might be important for once*/
	/*unhides unimportant buttons*/
	div.global-navigation__links, div.global-navigation__links {
	  visibility: visible !important;
	}
/*makes the sidebar full visible*/
div.global-navigation {
  opacity: 1 !important;
  box-shadow: 0 0px 0px 0 black;
  background-color: rgba(0,0,0,0)
}
.global-navigation__bottom {
  background-color: rgba(0,0,0,0)
}
}
