style.css:
@-moz-document domain("eksisozluk.com") {
body {
    background-color: #242424 !important;
    color: #b0b0b0 !important;
  }

  body > div {
    color: #e0e0e0 !important;
  }
  header {
    background-color: #242424 !important;
    border-bottom: 1px solid #333333 !important;
  }

  h1#title span {
    color: #e0e0e0 !important;
  }

  #entry-item-list .content {
    color: #b0b0b0 !important;
  }
  .topic-list.partial > li > a > small {
    right: 0.5rem;
  }
  .topic-list.partial > li > a {
    padding-left: 0.5rem;
    border-bottom: 1px solid transparent;
  }
  html.no-touch .topic-list li > a:hover {
    border-bottom: 1px solid #81c14b;
  }
  html.no-touch #partial-index::-webkit-scrollbar-thumb {
    background-color: #53a245 !important;
  }
  html.no-touch #partial-index::-webkit-scrollbar-thumb:hover {
    background-color: #81c14b !important;
  }
  html::-webkit-scrollbar {
    width: 8px;
  }
  html::-webkit-scrollbar-button {
    width: 0;
    height: 0;
    display: none;
  }
  html::-webkit-scrollbar-corner {
    background-color: transparent;
  }
  html::-webkit-scrollbar-thumb {
    background-color: #53a245 !important;
    box-shadow: inset 1px 1px 0 rgba(0, 0, 0, 0.1), inset 0 -1px 0 rgba(0, 0, 0, 0.07);
    border-radius: 4px;
  }
  html::-webkit-scrollbar-thumb:hover {
    background-color: #81c14b !important;
  }
  #pinned-icon > svg {
    fill: red !important;
  }

  a[href^='/biri/ek%c5%9fis%c3%b6zl%c3%bck'] {
    color: red !important;
    font-weight: bold;
  }

  #corporate-content-sponsored-entry {
    color: yellow;
  }
  li[data-author='reklam'] {
    display: none;
  }

  /* Eksisozluk Images */
  #pagewrap #leftwrap {
    margin: 0 auto !important;
    width: 100% !important;
  }

  #pagewrap #rightwrap {
    display: none !important;
  }

  #pagewrap header {
    display: none !important;
  }
  #image {
    width: 50%;
  }
  #bg {
    display: none !important;
  }
  #pagewrap #content {
    margin: 0 !important;
    width: 100%;
  }
  #pagewrap footer {
    margin: 0 !important;
  }

  #yeni-reklam {
    display: none !important;
  }
  html.no-touch .theme-enabled #main {
    padding-top: 0 !important;
  }
  html.no-touch .theme-enabled #index-section {
    padding-top: 0 !important;
  }
  article.unread p {
    color: #81c14b !important;
  }
  article.unread small {
    color: #81c14b !important;
  }
  article.unread {
    background-color: #1b1b1d;
  }
  #fixed-intro {
    background-color: unset;
  }
  #set-biography {
    display: none !important;
  }
  #set-biography textarea {
    height: 50px !important;
    width: 350px !important;
  }
}
