style.css:
@-moz-document domain("www.ifood.com.br"), url-prefix("https://www.ifood.com.br") {
div[data-reactroot],
.responsive-header,
.home-page-cardstack,
.content-container.cardstack-home,
.common-catalog-item--v3.common-catalog-item {
    background-color: #2b2b2b !important;
}

.cardstack-component-header__title,
.cardstack-component-header__title,
.round-image__title,
.round-image__title {
    color: white !important;
}

/* NAV */
.desktop-category-links__item>a,
.small-banner-item>.small-banner-item__title,
.delivery-input .delivery-input__address,
.header-cart .header-cart__cost,
.header-cart .header-cart__items {
    color: white !important;
}

.desktop-category-links__item--active>a {
    color: #ea1d2c !important;
}

/* END NAV */

/* BANNER */
.swiper-wrapper .swiper-slide a.small-banner-item {
    background-color: #1a1a1a !important;
    border: 1px solid white !important;
}

/* */

/* TIPOS DE COMIDA */
.common-catalog-item__info>.common-catalog-item__name,
.common-catalog-item__info>.catalog-item-v3-content__delivery,
.common-catalog-item__info>.common-catalog-item__value.common-catalog-item__price--regular,
.common-catalog-item__info>.common-catalog-item__value.common-catalog-item__price--variable {
    color: white !important;
}

.cardstack-filter.cardstack-filter--sticky,
.cardstack-filter.cardstack-filter--sticky .toggle-chip {
    background-color: #2b2b2b !important;
}

.toggle-chip>span,
.toggle-chip>svg {
    color: white !important;
}

/* */

/* LOJAS */
.swiper-wrapper .swiper-slide a.round-image,
.cardstack-context-message.cardstack-context-message--subscription,
.cardstack-context-message.cardstack-context-message--voucher {
    background-color: #1a1a1a !important;
}

.merchant-list-v2 .merchant-list-v2__item-wrapper .merchant-v2 {
    background-color: #1a1a1a !important;
    border: 1px solid white !important;
}

.merchant-list-v2 .merchant-list-v2__item-wrapper .merchant-v2 .merchant-v2__title,
.merchant-list-v2 .merchant-list-v2__item-wrapper .merchant-v2 .merchant-v2__content .merchant-v2__title .merchant-v2__name,
.merchant-list-v2 .merchant-list-v2__item-wrapper .merchant-v2 .merchant-v2__content .merchant-v2__title .merchant-v2__info,
.merchant-list-v2 .merchant-list-v2__item-wrapper .merchant-v2 .merchant-v2__content .merchant-v2__footer {
    color: white !important;
}

/* */

/* RECOMENDAÇÕES */
.swiper-wrapper .swiper-slide a.common-catalog-item,
.swiper-wrapper .swiper-slide a.common-catalog-item .common-catalog-item__container-image,
.swiper-wrapper .swiper-slide .merchant-carousel__item-wrapper {
    background-color: #1a1a1a !important;
}

.swiper-wrapper .swiper-slide a.common-catalog-item {
    border: 1px solid white !important;
}

.swiper-wrapper .swiper-slide .merchant-carousel__item-wrapper {
    border: 1px solid white !important;
}

.swiper-wrapper .swiper-slide .merchant-carousel__item-wrapper .merchant-name {
    color: white !important;
}

/* */
/* RESTAURANT PAGE */
.main-layout,
.main-layout .merchant-banner,
.main-layout .merchant-info,
.main-layout .gpt-ads-bg,
.main-layout .restaurant-promotions,
.main-layout .restaurant-menu__header,
.marmita-dropdown-menu .marmita-dropdown-menu__button {
    background-color: #2b2b2b !important;
}

.dish-card-wrapper .dish-card {
    box-shadow: 1px 1px 2px white;
    background-color: #2b2b2b !important;
}

.main-layout .merchant-info .merchant-info__title,
.marmita-dropdown-menu .marmita-dropdown-menu__button .btn__label,
.restaurant-menu-group .restaurant-menu-group__title,
.dish-card .dish-card__description {
    color: white !important;
}

.dish-card .dish-card__price {
    color: #50a773 !important;
}

.restaurant-menu__header-wrapper .restaurant-menu__menu-search {
  background-color: white !important;
}  

/* */
/* FOOTER */
.desktop-container.footer {
    background-color: #141414 !important;
}

.desktop-container.footer .footer__subtitle {
    color: white !important;
}

/* */



/* FOOTER */
  footer.desktop-container.footer {
    background-color: #141414 !important;
	}

  footer.desktop-container.footer h3.footer__subtitle {
    color: white !important;
	}

/* */
}
