style.css:
@-moz-document domain("www.ifood.com.br"), url-prefix("https://www.ifood.com.br") {
div[data-reactroot],
  header.responsive-header, 
	div.home-page-cardstack,
  div.content-container.cardstack-home, 
  a.round-image,
  a.small-banner-item,
	a.common-catalog-item--v3.common-catalog-item {
  	background-color: #2b2b2b !important;
	}

  h2.cardstack-component-header__title, 
  .cardstack-component-header__title,
	span.round-image__title, .round-image__title {
    color: white !important;
  }
  
/* NAV */
	li.desktop-category-links__item > a, 
  a.small-banner-item > .small-banner-item__title,
  button.delivery-input span.delivery-input__address,
	button.header-cart .header-cart__cost, 
	button.header-cart .header-cart__items  {
    color: white !important;
	}

  li.desktop-category-links__item--active > a {
    color: #ea1d2c !important;
	}

/* END NAV */

/* TIPOS DE COMIDA */
  div.common-catalog-item__info > h2.common-catalog-item__name,
  div.common-catalog-item__info > span.catalog-item-v3-content__delivery,
  div.common-catalog-item__info > span.common-catalog-item__value.common-catalog-item__price--regular,
	div.common-catalog-item__info > span.common-catalog-item__value.common-catalog-item__price--variable 
	{
    color: white !important;
  }
  
  ul.cardstack-filter.cardstack-filter--sticky,
	ul.cardstack-filter.cardstack-filter--sticky button.toggle-chip {
    background-color: #2b2b2b !important;
	}

  button.toggle-chip > span,
	button.toggle-chip > svg {
    color: white !important;
	}

/* */

/* LOJAS */
  div.merchant-list-v2 div.merchant-list-v2__item-wrapper div.merchant-v2 {
    background-color: #2b2b2b !important;
    border: 1px solid white !important;
	}

  div.merchant-list-v2 div.merchant-list-v2__item-wrapper div.merchant-v2 .merchant-v2__title,
  div.merchant-list-v2 div.merchant-list-v2__item-wrapper div.merchant-v2 div.merchant-v2__content .merchant-v2__title span.merchant-v2__name,
  div.merchant-list-v2 div.merchant-list-v2__item-wrapper div.merchant-v2 div.merchant-v2__content .merchant-v2__title .merchant-v2__info,
	div.merchant-list-v2 div.merchant-list-v2__item-wrapper div.merchant-v2 div.merchant-v2__content .merchant-v2__footer {
    color: white !important;
	}

/* */

/* FOOTER */
  footer.desktop-container.footer {
    background-color: #141414 !important;
	}

  footer.desktop-container.footer h3.footer__subtitle {
    color: white !important;
	}

/* */
}
