style.css:
@-moz-document domain("www.youtube.com"), url-prefix("https://www.youtube.com/") {
/*----------progrss bar----------*/
.ytp-scrubber-button{
  background-color: #fff0 !important;
  width: 200px !important;
  height: 100px !important;
  background-image: url("https://media.tenor.com/3ULSwSpaUnoAAAAi/hololive-tsunomaki-watame.gif") !important;
  background-repeat: no-repeat !important;
  background-position: center !important;
  bottom: 18px !important;
  margin-left: -165px !important;
  margin-top: -77px !important;
  transform: rotate(0deg) !important;
  transform: scale(0.9) !important;
  border-radius: 30px !important;
}
.ytp-scrubber-container{
  height: 10px !important;
}
.ytp-swatch-background-color{
  /*video's progress bar color*/
  background-color: #32974b;
}
.ytd-thumbnail-overlay-resume-playback-renderer {
  /*thumbnail's progress bar color*/
  background-color: #fffab6 !important;
}

/*----------text n hyperlinks----------*/
.yt-simple-endpoint {
  /*tags and timelines*/
  color: #d6c398 !important;
}
.yt-core-attributed-string__link,
#owner-sub-count, 
#channel-name,
yt-formatted-string.ytd-channel-name .yt-simple-endpoint,
.ytd-video-meta-block .inline-metadata-item:nth-child(3n){
  color: #d6c398 !important;
}
.ytd-channel-name .yt-simple-endpoint,
.ytd-comment-renderer,
ytd-author-comment-badge-renderer .ytd-channel-name {
  color: #fff !important;
}
}
