style.css:
@-moz-document url-prefix("https://login-learn.k12.com/") {
body {
  color: #333;
  background-color: #e2d8d8;
}

body.nextgen:not(.login-body) #side-nav #brand .school-logo img {
    background-image: url("https://cdn.donmai.us/original/f9/a0/f9a03349ad0e577bc6f9dfd627acb70b.jpg");
    background-size: 77px;
    width: 77px;
    height: 77px;
}

body.nextgen .events-table>tbody>tr>td a.event-link.electives,body.nextgen .events-table>tbody>tr>td a.event-link.buisiness {
    background-image: url("https://cdn.donmai.us/original/f9/a0/f9a03349ad0e577bc6f9dfd627acb70b.jpg");
    background-size: 700px;
    background-position-y: 490px;
}

body.nextgen:not(.login-body) #header-bar {
    -webkit-backface-visibility: hidden;
    width: calc(100% - 240px);
    min-height: 90px;
    background-color: #eed9d9;
    z-index: 150;
    position: fixed;
    -webkit-box-shadow: 0 3px 0 rgba(0,0,0,.05);
    -moz-box-shadow: 0 3px 0 rgba(0,0,0,.05);
    box-shadow: 0 3px 0 rgba(0,0,0,.05)
}

body.nextgen:not(.login-body) #side-nav {
    position: fixed;
    z-index: 100;
    display: inline-block;
    margin: 0;
    padding: 13px;
    background-color: #e5c9c9;
    width: 240px;
    -webkit-transition: width .3s ease;
    -moz-transition: width .3s ease;
    -o-transition: width .3s ease;
    transition: width .3s cubic-bezier(0.42, 0, 0.1, 0.91);
    height: 100vh;
    float: left
}

body.nextgen:not(.login-body) #copyright #legal-btn {
    display: block;
    color: #985252;
    height: auto
}
body.nextgen:not(.login-body) #copyright {
    bottom: 0;
    padding: 0 0 35px 15px;
    position: static;
    color: #d58181;
}
body.nextgen:not(.login-body) #copyright #legal ul li a {
    color: #a06f6f;
    text-decoration: underline;
    height: 25px
}

body.nextgen:not(.login-body) #side-nav #brand .school-logo img {
    width: 77px;
    height: 77px
}

body.nextgen:not(.login-body) #side-nav #brand .school-title div:nth-child(1) {
    text-align: center;
    text-transform: uppercase;
    color: #fff;
    font-size: 20px;
    font-weight: 700;
    margin: 0 0 4px;
}

body.nextgen:not(.login-body) #side-nav #sidebar-wrapper li a i {
    color: #b78e8e;
    font-size: 2.1em;
    display: inline-block;
    vertical-align: middle;
    margin-right: 8px;
    line-height: 28px
}

body.nextgen:not(.login-body) #side-nav #brand .school-logo {
    position: relative;
    margin: 0 auto;
    padding: 0;
    height: 84px;
    width: 84px;
    width: 84px;
    height: 84px;
    background: #626e7c;
    -webkit-border-radius: 56px;
    -moz-border-radius: 56px;
    border-radius: 56px;
    border: 4px solid #443743;
    overflow: hidden
}

body.nextgen:not(.login-body) #side-nav #sidebar-wrapper li a:hover {
    color: #fff9a7
}

body.nextgen:not(.login-body) #side-nav #sidebar-wrapper li a:hover .icon {
    color: #fff9a7
}

body.nextgen .events-table>tbody>tr>td a.event-link {
    position: relative;
    display: block;
    width: 100%;
