/* Custom Scrollbar Styles */
::-webkit-scrollbar {
    width: 5px;
    height: 8px;
}

::-webkit-scrollbar-track {
    background: #f0f0f0;
    border-radius: 10px;
}

::-webkit-scrollbar-thumb {
    background: #d0d0d0;
    border-radius: 10px;
    transition: background-color 0.4s ease;
}

::-webkit-scrollbar-thumb:hover {
    background: #c0c0c0;
}

::-webkit-scrollbar-corner {
    background: #f0f0f0;
}
