     --semiTransWhite: #ffffff2b;
     --KuromiA: #8260a2;
     --KuromiB: #b18fbf;
}


.logo {
  max-width: 100%;
  content: url(https://i.ibb.co/ySNQt8t/logo3.png);
}

.wrap {
    padding-top: 223px;
}

/*.content {
  padding: 20px;
  background: #1e1e1e;
}.fleft {
  padding: 20px;
  background: #1e1e1e;
}*/body {
  background: #111 url(https://i.ibb.co/p1dX0VY/bg.png) center top no-repeat;
