.th-mask {
  background-color: rgba(0,0,0,0.6);
  backdrop-filter: blur(2px);
}.th-mask .fa {
  border-radius: 50%;
  color: #fff;
  background-color: #ae8fcc;
    /*backdrop-filter: blur(25px);*/
}

.th-series {
  background-color: #8260a24f;
    backdrop-filter: blur(25px);
  color: #fff;
}

.button34 {
  background-color: #9e6fb1;
  border: none;
  color: white;
  border-radius: 2px;
}

.fplayer .tabs-sel span.current {
  background-color: #8260a2;
  color: #fff;
}

.light-off .flight::after {
  left: 21px;
  background-color: #8260a2;
}
.flight::after {
  background: #e4c6ff;
}


.navigation span, .pagi-nav a:hover {
  background-color: #8260a2;
  color: #fff;
}

.comm-one > span.comm-author, .comm-one > span.comm-author a {
  font-weight: 600;
  color: #4e2f73;
  opacity: 1;
}.comm-rate3 > span > span.ratingplus {
  color: #4e2f73;
}
}

options.value:
/* Темная тема */
.header {
  background-color:var(--TransBlack) !important;
  backdrop-filter: blur(25px) !important;
}.flist li > span:first-child {
  font-weight: 600;
  color: Var(--White);
}.content {
  padding: 20px;
  background:Var(--Background);
}.carou-sect {
  background-color: Var(--Background);
}.slide-circle {
  width: 50px;
  height: 50px;
  border-radius: 50%;
  margin: -25px 0 0 -25px;
  background-color: #00000024;
  box-shadow: 0 0 0 5px #00000024,0 0 12px 0 rgba(0,0,0,0.8);
  backdrop-filter: blur(5px);
}.fx-1 {
  background: Var(--Background);
}.slice-masked::before {
  background: linear-gradient(to bottom, rgba(255,255,255,0) 0%, Var(--Background) 100%);
}.fshare {
    background: Var(--Background);
    border-top: 1px solid #e3e3e3;
}.rating-emoji {
  display: flex;
  background: Var(--Background);
  border-radius: 0px;
  padding: 0 1%;
  flex-wrap: wrap;
}.rating-emoji-item-count {
  display: block;
  font-size: 13px;
  line-height: 20px;
  margin-top: 3px;
  opacity: 0.7;
  color: Var(--White);
}.navigation a, .navigation span, .pnext a, .pprev a, .pprev > span, .pnext > span {
    border-radius: 5px;
  box-shadow: inset 0 0 0 0px Var(--Text);
  background-color: var(--semiTransWhite);
}.navigation span.nav_ext {
  background: var(--semiTransWhite);
  color: Var(--Text1);
    border-radius: 5px;
}a:hover, a:focus {
  color: var(--KuromiB)!important;
}a {
  color: Var(--KuromiA) !important;
  text-decoration: none;
}body {
  background: #111 url(https://i.ibb.co/p1dX0VY/bg.png) center top no-repeat;
    color: Var(--TextW1)!important;
}.hidden-menu {
  background-color:var(--TransBlack)!important;
    backdrop-filter: blur(25px);
  border-top: 3px solid var(--KuromiA);
}.hm-col li {
  color: var(--KuromiB)!important;
}.bb-pane {
  background: Var(--Background);
  border: 1px solid #8880;
}.bb-btn {
  display: inline-block;
  overflow: hidden;
  padding: 4px 10px;
  border: 1px solid #e3e3e3;
  box-shadow: inset 0 1px 2px #fff;
  background: #fff0;
    background-color: rgba(0, 0, 0, 0);
}
