style.css:
@-moz-document url-prefix("https://lordserials.fan") {
:root {
     /* TST не ясно что, но почему-то было покрашено */
    --TST: #f00;
    --semiTransWhite: #ffffff82;
    --TransWhite: #ffffff82;
    --semiTransBlack: #000000bf;
    --TransBlack: #0000001a;
    --Black: #000000d1;
    --KuromiA: #8260a2;
    --KuromiA1:#8260a24f;
    --KuromiB: #b18fbf;
    --KuromiB1: #ae8fcc;
    --KuromiC: #4e2f73;
    --KuromiC1: #e4c6ff;
    --KuromiD:#9e6fb1;
    --Text: #1c1c1c;
    --TextW: #eaeaea;
    --TextW1: #d5d5d5;
    --Text1: #2d2d2d;
    --Text2: #fff;
    --White: #fff;
    --Background: #1e1e1e;
    --Backgroun: #f00;
}

/* Логотип в шапке */
.logo {
  max-width: 100%;
  content: url(https://i.ibb.co/ySNQt8t/logo3.png);
}

/* Темная тема */ /*
.header {
  background-color:var(--TransBlack) !important;
  backdrop-filter: blur(25px) !important;
}.flist li > span:first-child {
  font-weight: 600;
  color: Var(--White);
}.content {
  padding: 20px;
  background:Var(--Background);
}.carou-sect {
  background-color: Var(--Background);
}.slide-circle {
  width: 50px;
  height: 50px;
  border-radius: 50%;
  margin: -25px 0 0 -25px;
  background-color: #00000024;
  box-shadow: 0 0 0 5px #00000024,0 0 12px 0 rgba(0,0,0,0.8);
  backdrop-filter: blur(5px);
}.fx-1 {
  background: Var(--Background);
}.slice-masked::before {
  background: linear-gradient(to bottom, rgba(255,255,255,0) 0%, Var(--Background) 100%);
}.fshare {
    background: Var(--Background);
    border-top: 1px solid #e3e3e3;
}.rating-emoji {
  display: flex;
  background: Var(--Background);
  border-radius: 0px;
  padding: 0 1%;
  flex-wrap: wrap;
}.rating-emoji-item-count {
  display: block;
  font-size: 13px;
  line-height: 20px;
  margin-top: 3px;
  opacity: 0.7;
  color: Var(--White);
}.navigation a, .navigation span, .pnext a, .pprev a, .pprev > span, .pnext > span {
    border-radius: 5px;
  box-shadow: inset 0 0 0 0px Var(--Text);
  background-color: var(--semiTransWhite);
}.navigation span.nav_ext {
  background: var(--semiTransWhite);
  color: Var(--Text1);
    border-radius: 5px;
}a:hover, a:focus {
  color: var(--KuromiB)!important;
}a {
  color: Var(--KuromiA) !important;
  text-decoration: none;
}body {
  background: #111 url(https://i.ibb.co/p1dX0VY/bg.png) center top no-repeat;
    color: Var(--TextW1)!important;
}.hidden-menu {
  background-color:var(--TransBlack)!important;
    backdrop-filter: blur(25px);
  border-top: 3px solid var(--KuromiA);
}.hm-col li {
  color: var(--KuromiB)!important;
}.bb-pane {
  background: Var(--Background);
  border: 1px solid #8880;
}.bb-btn {
  display: inline-block;
  overflow: hidden;
  padding: 4px 10px;
  border: 1px solid #e3e3e3;
  box-shadow: inset 0 1px 2px #fff;
  background: #fff0;
    background-color: rgba(0, 0, 0, 0);
}

/* Фон над шапкой */
body {
  background: #111 url(https://i.ibb.co/p1dX0VY/bg.png) center top no-repeat;
    color: Var(--Text1);
}
/* Отступ фона */
.wrap {
    padding-top: 223px;
}

/* Логотип в конце страницы */
.ft-desc {
  width: 50%;
  padding-top: 55px;
  background: url(https://i.ibb.co/vD4Rhvb/ft-logo3.png) left -15px no-repeat;
}

/* Фон шапки */
.header {
  background-color:var(--semiTransWhite);
  backdrop-filter: blur(25px);
}

/* цвет выделения в шапке */
.hmenu > li:hover > a, .hmenu > li > a.is-active {
  opacity: 1;
  box-shadow: inset 0 -3px var(--KuromiA);
  color: var(--KuromiA);
}

/* Выпадающее меню */
.hidden-menu {
  background-color:var(--semiTransWhite);
    backdrop-filter: blur(25px);
  border-top: 3px solid var(--KuromiA);
}.hm-col li {
  color: var(--Black);
}

/* цвет выделения ссылок + Текст в шапке */
a:hover, a:focus {
  color: var(--KuromiA);
}a {
  color: Var(--Text);
  text-decoration: none;
}

/* Акцент в выпадающем меню */
.hm-col a:hover {
  background-color: var(--KuromiA);
  color: Var(--Text2);
  opacity: 1;
}

input[type="text"], input[type="password"], select, textarea {
  width: 100%;
  background-color: var(--semiTransWhite);
  color: #000;
  box-shadow: inset 0 0 0 1px #e3e3e3, inset 1px 2px 5px rgba(0,0,0,0.2);
}
/* Цвет иконки поиска */
.search-box button {
  color: var(--KuromiA) !important;
}

/* Кнопка "ВОЙТИ" */
.button:hover, .btn:hover, .up-second li a:hover, .usp-edit a:hover, .qq-upload-button:hover, .pagi-load a:hover, .usp-btn a:hover, button:not(.color-btn):hover, html input[type="button"]:hover, input[type="reset"]:hover, input[type="submit"]:hover, .meta-fav a:hover {
  background-color: var(--KuromiA);
    opacity: 0.85;
  color: Var(--Text2);
}.button, .btn, .pagi-load a, .up-second li a, .usp-edit a, .qq-upload-button, button:not(.color-btn):not([class*="fr"]), html input[type="button"], input[type="reset"], input[type="submit"], .meta-fav a, .usp-btn a {
  box-shadow: inset 0 -3px var(--KuromiB1);
  color: Var(--Text2);
  background-color: var(--KuromiA);
}

/* Окно Авторизации */
.login-overlay, .mmenu-overlay {
  opacity: 1;
  background-color: Var(--semiTransBlack);
  backdrop-filter: blur(25px);
}.login-box {
     border-radius: 25px;
  background-color: Var(--semiTransWhite);
  border-top: 5px solid Var(--KuromiA);
  box-shadow: 0 20px 60px Var(--Black);
}.login-check span::after {
  background: Var(--KuromiA);
}.login-check span {
  line-height: 30px;
  color: Var(--Text1);
}

/*  */
.sect-title {
  background-color: Var(--KuromiA);
  color: Var(--Text2) !important;
  margin-right: 20px;
}
/*  */
.th-mask {
  background-color: rgba(0,0,0,0.6);
  backdrop-filter: blur(2px);
}.th-mask .fa {
  border-radius: 50%;
  color: Var(--Text2);
  background-color: var(--KuromiB1);
    /*backdrop-filter: blur(25px);*/
}

/*  */
.th-series {
  background-color: Var(--KuromiA1);
    backdrop-filter: blur(25px);
  color: Var(--Text2);
}

/*  */
.button34 {
  background-color: var(--KuromiD);
  border: none;
  color: Var(--White);
  border-radius: 2px;
}

/*  */
.fplayer .tabs-sel span.current {
  background-color: Var(--KuromiA);
  color: Var(--Text2);
}

/* Кнопка СВЕТ */
.light-off .flight::after {
  left: 21px;
  background-color: Var(--KuromiA);
}
.flight::after {
  background:  var(--KuromiC1);
}
.light-overlay {
  background-color: Var(--semiTransBlack);
  backdrop-filter: blur(10px);
}

/*  */
.navigation span, .pagi-nav a:hover {
  background-color: Var(--KuromiA);
  color: var(--White);
}

/*  */
.comm-one > span.comm-author, .comm-one > span.comm-author a {
  font-weight: 600;
  color: Var(--KuromiC);
  opacity: 1;
}.comm-rate3 > span > span.ratingplus {
  color:Var(--KuromiC);
}

/* Панель-разделитель в мобильной версии */
.side-panel .hidden-menu {
  border-bottom: 3px solid Var(--KuromiA);
}
}

