style.css:
@-moz-document domain("animego.org") {
.py-3:nth-child(5) {
background-image:url(https://images.wallpaperscraft.ru/image/single/devushka_velosiped_siluet_989860_1920x1080.jpg);
background-size:100%;
}

.border-top-0:nth-child(2) {
background-color:transparent;
}

#content {
background-color:transparent;
backdrop-filter:blur(50px);
color:#FFF;
border-radius:15px 15px 0 0;
box-shadow:0 8px 8px rgba(0,0,0,0.3);
}

.list-group-item-action {
width:100%;
color:#FFF;
text-align:inherit;
transition:transform .3s ease;
}

.list-group-item-action:hover {
transform:scale(1.1);
}

.carousel-horizontal-container {
background-color:rgba(0,0,0,0.2);
border-radius:20px;
box-shadow:0 8px 8px rgba(0,0,0,0.3);
}

#wrap > .d-lg-block {
background-image:url(https://sun9-80.userapi.com/impg/Nc91ibKPC18qsQpZCAatXssLXcQ962EDnlTKGQ/J2-J9_ia-_Q.jpg?size=1920x1080&quality=96&sign=aa7c7aced570362d0afa52c3dd71b32a&type=album);
background-size:100%;
}

#video-watch {
background-image:url(https://sun9-80.userapi.com/impg/Nc91ibKPC18qsQpZCAatXssLXcQ962EDnlTKGQ/J2-J9_ia-_Q.jpg?size=1920x1080&quality=96&sign=aa7c7aced570362d0afa52c3dd71b32a&type=album);
background-size:100%;
background-attachment:fixed;
background:rgba(0,0,0,0.3);
box-shadow:0 8px 8px rgba(0,0,0,0.3);
}

#video-watch:before,#video-watch:after {
content:'';
position:absolute;
background-color:rgba(0,0,0,0.3);
width:100%;
height:100%;
top:0;
}

.content:nth-child(6) {
background-color:transparent;
backdrop-filter:blur(50px);
color:#FFF;
border-radius:0 0 15px 15px;
box-shadow:0 8px 8px rgba(0,0,0,0.3);
}

.bg-dark:nth-child(3) {
background-color:rgba(0,0,0,0.3)!important;
}

#sorter-sort {
color:#FFF;
border:none;
background-color:rgba(0,0,0,0.3);
}

.mb-lg-0 > .card,.mb-md-0 > .card,.col-lg-4 > .card {
background-color:transparent;
backdrop-filter:blur(50px);
color:#FFF;
border-radius:15px;
box-shadow:0 8px 8px rgba(0,0,0,0.5);
}

.border-top-0:nth-child(1),#slide-toggle-1 > .last-update-item,#slide-toggle-saturday,#slide-toggle-saturday > .list-group-item:nth-child(1),#slide-toggle-saturday > .list-group-item:nth-child(2),#slide-toggle-saturday > .list-group-item:nth-child(3),#slide-toggle-saturday > .list-group-item:nth-child(4),#slide-toggle-saturday > .list-group-item:nth-child(5),#slide-toggle-saturday > .list-group-item:nth-child(6),#slide-toggle-saturday > .list-group-item:nth-child(7),#slide-toggle-saturday > .list-group-item:nth-child(8),#slide-toggle-saturday > .list-group-item:nth-child(9),#slide-toggle-saturday > .list-group-item:nth-child(10),#slide-toggle-saturday > .list-group-item:nth-child(11),.card > .list-group-item,#slide-toggle-monday > .list-group-item,#slide-toggle-tuesday > .list-group-item,#slide-toggle-wednesday > .list-group-item,#slide-toggle-thursday > .list-group-item,#slide-toggle-friday > .list-group-item {
background-color:transparent;
color:#FFF;
}

.text-gray-dark-5,.py-3:nth-child(2) .col-12,.nav-item > .text-link-gray,.col-6,.font-weight-normal,.col-12 > .b-tooltipped,div > span > .text-link-gray > span,.mt-2 > .d-none {
color:#FFF;
}

.col-12 > .animes-list-item,#tns1-item0,#tns1-item1,#tns1-item2,#tns1-item3,#tns1-item4,#tns1-item5,#tns1-item6 {
transition:transform .3s ease;
}

.col-12 > .animes-list-item:hover,#tns1-item0:hover,#tns1-item1:hover,#tns1-item2:hover,#tns1-item3:hover,#tns1-item4:hover,#tns1-item5:hover,#tns1-item6:hover {
transform:scale(1.05);
}

.pt-3,.py-3,#wrap,body,.video-player-side {
background-image:url(https://sun9-80.userapi.com/impg/Nc91ibKPC18qsQpZCAatXssLXcQ962EDnlTKGQ/J2-J9_ia-_Q.jpg?size=1920x1080&quality=96&sign=aa7c7aced570362d0afa52c3dd71b32a&type=album);
background-size:100%;
background-attachment:fixed;
}

.content:nth-child(5),.content:nth-child(4) {
background-color:transparent;
backdrop-filter:blur(50px);
color:#FFF;
box-shadow:0 8px 8px rgba(0,0,0,0.3);
}

.list-group-item,.modal-content {
background-color:transparent;
backdrop-filter:blur(50px);
color:#FFF;
}

#username,#password {
border:none;
background-color:rgba(0,0,0,0.3);
}
}
