style.css:
/* Establecer el fondo rosado */
body {
    background-color: #FFC0CB;
    font-family: 'Comic Sans MS', cursive, sans-serif;
}

/* Establecer color rosado en los textos */
* {
    color: #FF69B4 !important;
    font-family: 'Comic Sans MS', cursive, sans-serif;
}

/* Cambiar la apariencia de los botones */
button, input[type="submit"], input[type="button"] {
    background-color: #FFB6C1;
    border: 2px solid #FF69B4;
    color: white;
    font-family: 'Comic Sans MS', cursive, sans-serif;
    padding: 10px;
    border-radius: 5px;
}

button:hover, input[type="submit"]:hover, input[type="button"]:hover {
    background-color: #FF69B4;
}

/* Cambiar la apariencia de los enlaces */
a {
    color: #FF69B4;
    text-decoration: underline;
    font-family: 'Comic Sans MS', cursive, sans-serif;
}

a:hover {
    color: #FF1493;
}

/* Agregar im√°genes de Hello Kitty */
body::before {
    content: "";
    background-image: url('https://www.pngall.com/wp-content/uploads/5/Hello-Kitty-Face-PNG-Picture.png');
    background-repeat: no-repeat;
    background-position: top right;
    position: fixed;
    top: 10px;
    right: 10px;
    width: 150px;
    height: 150px;
    z-index: 1000;
    opacity: 0.8;
}

body::after {
    content: "";
    background-image: url('https://www.pngall.com/wp-content/uploads/5/Hello-Kitty-PNG-High-Quality-Image.png');
    background-repeat: no-repeat;
    background-position: bottom left;
    position: fixed;
    bottom: 10px;
    left: 10px;
    width: 150px;
    height: 150px;
    z-index: 1000;
    opacity: 0.8;
}
