style.css:
/* Dark Theme */
body {
    background-color: #121212; /* Dark background color */
    color: #e0e0e0; /* Light text color */
    font-family: 'Arial', sans-serif;
}

a {
    color: #bb86fc; /* Light purple for links */
    text-decoration: none;
}

a:hover {
    color: #ffffff; /* White color on hover */
    text-decoration: underline;
}

header, nav, footer {
    background-color: #1f1f1f; /* Slightly lighter background for headers and footers */
    padding: 20px;
    border-bottom: 1px solid #333; /* Subtle border for separation */
}

button, input[type="submit"], input[type="button"] {
    background-color: #333333; /* Dark button background */
    color: #ffffff; /* Light button text */
    border: 1px solid #555555;
    padding: 10px 20px;
    cursor: pointer;
}

button:hover, input[type="submit"]:hover, input[type="button"]:hover {
    background-color: #444444; /* Slightly lighter background on hover */
}

input, textarea {
    background-color: #1e1e1e; /* Dark background for input fields */
    color: #e0e0e0; /* Light text color */
    border: 1px solid #333333;
    padding: 10px;
}

input:focus, textarea:focus {
    border-color: #bb86fc; /* Purple border on focus */
    outline: none;
}

h1, h2, h3, h4, h5, h6 {
    color: #ffffff; /* White headers */
}

p {
    line-height: 1.6;
}

table {
    width: 100%;
    border-collapse: collapse;
}

th, td {
    padding: 10px;
    border: 1px solid #333333;
}

th {
    background-color: #1f1f1f; /* Darker background for table headers */
    color: #ffffff;
}

td {
    background-color: #2a2a2a; /* Slightly lighter for table cells */
}

blockquote {
    border-left: 4px solid #bb86fc; /* Purple left border */
    padding-left: 15px;
    color: #e0e0e0;
    background-color: #1e1e1e;
}

code {
    background-color: #1e1e1e;
    color: #ffb86c; /* Light orange for code text */
    padding: 2px 4px;
    border-radius: 4px;
}

/* Custom scrollbar */
::-webkit-scrollbar {
    width: 10px;
}

::-webkit-scrollbar-thumb {
    background-color: #333333; /* Darker scrollbar */
    border-radius: 10px;
}

::-webkit-scrollbar-track {
    background-color: #121212; /* Darker track */
}
