style.css:
/* Pink Soft Theme */
body {
    background-color: #ffe6f0; /* Fondo rosado suave */
    color: #333; /* Color de texto para buena legibilidad */
}

a {
    color: #ff6699; /* Enlaces en rosa suave */
}

a:hover {
    color: #ff3366; /* Enlaces en rosa más fuerte al pasar el mouse */
}

header, nav, footer {
    background-color: #ffccdd; /* Fondo de encabezados y pie de página en rosa pastel */
    color: #fff; /* Texto en blanco */
    border-bottom: 2px solid #ff99bb; /* Borde inferior en rosa suave */
}

button, input[type="submit"], input[type="button"] {
    background-color: #ff99bb; /* Botones en rosa suave */
    color: #fff; /* Texto en blanco */
    border: none;
    padding: 10px 20px;
    border-radius: 5px;
    transition: background-color 0.3s ease;
}

button:hover, input[type="submit"]:hover, input[type="button"]:hover {
    background-color: #ff6699; /* Cambio de color en botones al pasar el mouse */
}

input[type="text"], input[type="email"], textarea {
    background-color: #fff0f5; /* Campos de texto en rosa pálido */
    border: 1px solid #ffccdd;
    padding: 10px;
    border-radius: 5px;
    color: #333;
}

h1, h2, h3, h4, h5, h6 {
    color: #ff6699; /* Títulos en rosa suave */
    font-family: 'Arial', sans-serif;
}

blockquote {
    background-color: #ffccdd; /* Citas en rosa pastel */
    border-left: 4px solid #ff99bb;
    padding: 10px 15px;
    color: #555;
}

table {
    background-color: #fff0f5; /* Tablas en rosa pálido */
    border-collapse: collapse;
    width: 100%;
}

th, td {
    border: 1px solid #ffccdd;
    padding: 10px;
    text-align: left;
}

th {
    background-color: #ff99bb; /* Encabezados de tabla en rosa suave */
    color: #fff;
}

tr:nth-child(even) {
    background-color: #ffe6f0; /* Filas alternas en rosa claro */
}
