style.css:
/* Pink Soft Theme - Expanded Version */
body {
    background-color: #f8e8e8; /* Fondo suave beige rosado */
    color: #3e3e3e; /* Texto en gris oscuro para mejor legibilidad */
    font-family: 'Helvetica Neue', Arial, sans-serif;
    line-height: 1.6;
    margin: 0;
    padding: 0;
}

/* Links */
a {
    color: #d16b8f; /* Enlaces en rosa suave */
    text-decoration: none;
}

a:hover {
    color: #c24b74; /* Color más oscuro al pasar el mouse */
    text-decoration: underline;
}

/* Headers */
header {
    background-color: #e3c6cf; /* Fondo de encabezado en rosa pastel */
    color: #fff;
    padding: 20px 0;
    text-align: center;
    border-bottom: 3px solid #d16b8f;
}

header h1, header h2, header h3 {
    font-family: 'Georgia', serif;
    color: #fff;
    margin: 0;
}

/* Navigation */
nav {
    background-color: #d16b8f; /* Barra de navegación en rosa suave */
    color: #fff;
    padding: 15px;
    text-align: center;
}

nav a {
    color: #fff;
    margin: 0 15px;
    text-transform: uppercase;
    font-weight: bold;
}

nav a:hover {
    color: #f5c2d2; /* Efecto hover en los enlaces de navegación */
}

/* Main Content */
main {
    padding: 30px;
    background-color: #f8e8e8;
    min-height: calc(100vh - 60px);
}

h1, h2, h3, h4, h5, h6 {
    color: #b93a5e; /* Títulos en rosa oscuro */
    font-family: 'Georgia', serif;
    margin-bottom: 20px;
}

p {
    margin-bottom: 20px;
    color: #4f4f4f; /* Texto de párrafo en gris oscuro */
}

/* Buttons */
button, input[type="submit"], input[type="button"] {
    background-color: #d16b8f; /* Botones en rosa suave */
    color: #fff;
    border: 1px solid #c24b74;
    padding: 10px 20px;
    border-radius: 5px;
    cursor: pointer;
    transition: background-color 0.3s ease;
}

button:hover, input[type="submit"]:hover, input[type="button"]:hover {
    background-color: #b93a5e; /* Cambio de color al pasar el mouse */
}

/* Form Inputs */
input[type="text"], input[type="email"], textarea {
    background-color: #ffe6ef; /* Campos de texto en rosa pálido */
    border: 1px solid #e3c6cf;
    padding: 10px;
    border-radius: 5px;
    color: #3e3e3e;
    width: 100%;
    box-sizing: border-box;
}

input[type="text"]:focus, input[type="email"]:focus, textarea:focus {
    border-color: #d16b8f; /* Borde de los inputs al enfocarlos */
    outline: none;
}

/* Blockquote */
blockquote {
    background-color: #ffe6ef; /* Fondo de cita en rosa suave */
    border-left: 5px solid #d16b8f;
    margin: 20px 0;
    padding: 10px 20px;
    color: #555;
    font-style: italic;
}

/* Tables */
table {
    background-color: #fff4f4; /* Fondo de tabla en rosa muy pálido */
    border-collapse: collapse;
    width: 100%;
    margin-bottom: 20px;
}

th, td {
    border: 1px solid #e3c6cf;
    padding: 12px;
    text-align: left;
}

th {
    background-color: #d16b8f; /* Encabezados de tabla en rosa suave */
    color: #fff;
}

tr:nth-child(even) {
    background-color: #f8e8e8; /* Filas pares en un fondo suave */
}

/* Footer */
footer {
    background-color: #e3c6cf; /* Fondo del pie de página en rosa pastel */
    color: #fff;
    text-align: center;
    padding: 20px 0;
    border-top: 3px solid #d16b8f;
}

footer p {
    margin: 0;
    font-size: 14px;
}
