style.css:
@-moz-document domain("mail.google.com") {
/* General background and text color */
    body, .aeF, .aeH, .nH, .nH .BltHke, .nH .Bk, .b8.UC, .Hq.aIF, .Hq.aIF.aRZ, .gbqfh {
        background-color: #000000 !important;
        color: #00FF00 !important;
    }

    /* Header, footer, and buttons */
    .gb_q, .gb_ve, .gb_Jd, .gb_Se, .gb_sc, .gb_Zc {
        background-color: #000000 !important;
        color: #00FF00 !important;
    }

    /* Links */
    a, .yP, .zF, .gD, .T-Jo {
        color: #00FF00 !important;
    }

    /* Email list items and text */
    .Cp .xY, .Cp .xY .y2, .Cp .xY .xY span, .xT .y6, .yX, .yW {
        background-color: #000000 !important;
        color: #00FF00 !important;
    }

    /* Selected or hovered email rows */
    .zA:hover, .zA[selected], .zA:focus {
        background-color: #003300 !important;
    }

    /* Sidebar (menu) */
    .aeN, .TN, .aKz, .aKw, .aeF, .aKh, .aio, .TO, .TN, .TN .TK, .TN .Tn {
        background-color: #000000 !important;
        color: #00FF00 !important;
    }

    /* Compose button and other action buttons */
    .T-I, .T-I-KE, .T-I-J3, .T-I-JW, .T-I-JO {
        background-color: #000000 !important;
        border: 1px solid #00FF00 !important;
        color: #00FF00 !important;
    }

    /* Email content background and text (including compose view) */
    .aeF > div, .ii, .a3s, .a3s .gmail_default, .ii.gt, .Am.Al.editable.LW-avf, .editable {
        background-color: #000000 !important;
        color: #00FF00 !important;
    }

    /* Inputs, textareas, and typing fields */
    input, textarea, .editable {
        background-color: #000000 !important;
        color: #00FF00 !important;
        caret-color: #00FF00 !important; /* Blinking cursor when typing */
    }

    /* Search bar */
    .gb_Se, .gb_We {
        background-color: #000000 !important;
        color: #00FF00 !important;
        border: 1px solid #00FF00 !important;
    }

    /* Scrollbar styling */
    ::-webkit-scrollbar {
        width: 10px;
        background-color: #000000;
    }

    ::-webkit-scrollbar-thumb {
        background-color: #00FF00;
    }

    /* Remove shadows and borders for a flat, terminal look */
    * {
        box-shadow: none !important;
        border-color: #00FF00 !important;
    }

    /* Inline email actions (archive, delete, etc.) */
    .ar9, .ar8, .amF, .amH, .ajz, .amj, .apU, .apV, .aoI, .aP6, .amM, .apS, .apW {
        color: #00FF00 !important;
    }

    /* Pop-up and modal backgrounds */
    .b8.UC, .Hq.aIF, .Hq.aIF.aRZ, .gbqfh, .aeN {
        background-color: #000000 !important;
        color: #00FF00 !important;
    }

    /* Label and folder names */
    .aim, .aim .nU, .ain, .aio, .aic, .asf {
        background-color: #000000 !important;
        color: #00FF00 !important;
    }

    /* Adding a blinking cursor next to the Gmail logo */
    #gbq1, .gb_vd {
        position: relative;
    }

    #gbq1::after, .gb_vd::after {
        content: '|';
        position: absolute;
        top: 0;
        left: 5px; /* Adjust the position based on your need */
        color: #00FF00;
        animation: blink-caret 1s step-end infinite;
    }

    /* Blinking cursor animation */
    @keyframes blink-caret {
        50% {
            opacity: 0;
        }
    }
}
