style.css:
@-moz-document domain("mail.google.com") {
/* Set the base colors */
    :root {
        --bg-color: #000000;
        --text-color: #00FF00;
        --highlight-color: #003300;
        --border-color: #00FF00;
    }

    /* General background and text color */
    body, .aeF, .aeH, .nH, .nH .BltHke, .nH .Bk, .b8.UC, .Hq.aIF, .Hq.aIF.aRZ, .gbqfh,
    .aeN, .aim, .ain, .aio, .aic, .ar9, .ar8, .amF, .amH, .ajz, .amj, .apU, .apV, .aoI,
    .aP6, .amM, .apS, .apW, .asf, .gb_sc, .gb_Zc, .gb_Ic, .gb_9d, .gb_6c,
    .aKk, .aKl, .aKm, .bhZ, .bym, .J-M, .J-M-JJ, .aji, .aj2, .aj3, .aol,
    .gb_Cd, .gb_Dd, .gb_Ed, .gb_5d, .gb_1d, .gb_2d, .gb_3d, .gb_4d {
        background-color: var(--bg-color) !important;
        color: var(--text-color) !important;
    }

    /* Links and font color */
    a, .yP, .zF, .gD, .T-Jo, .yW, .bq9, .w-asV.aiw, .aU6, .ajl, .ajm {
        color: var(--text-color) !important;
    }

    /* Email list items and text */
    .Cp .xY, .Cp .xY .y2, .Cp .xY .xY span, .xT .y6, .yX, .yW, .xY, .xY span, .yX, .aJ9 {
        background-color: var(--bg-color) !important;
        color: var(--text-color) !important;
    }

    /* Selected or hovered email rows */
    .zA:hover, .zA[selected], .zA:focus {
        background-color: var(--highlight-color) !important;
        color: var(--text-color) !important;
    }

    /* Extend highlight across the entire row */
    .zA {
        border-top: 1px solid var(--border-color) !important;
        border-bottom: 1px solid var(--border-color) !important;
    }

    /* Buttons and interactive elements */
    .T-I, .T-I-KE, .T-I-J3, .T-I-JW, .T-I-JO, .ams, .amn, .amO, .amH {
        background-color: var(--bg-color) !important;
        border: 1px solid var(--border-color) !important;
        color: var(--text-color) !important;
    }

    /* Input fields and editable areas */
    input, textarea, .editable, .aaZ, .bog, .w-asV.aiw {
        background-color: var(--bg-color) !important;
        color: var(--text-color) !important;
        caret-color: var(--text-color) !important;
        border: 1px solid var(--border-color) !important;
    }

    /* Sidebar and menu items */
    .aeN, .TN, .aKz, .aKw, .aKh, .TO, .TN .TK, .TN .Tn, .J-Ke, .J-Ke.n0, .bq9 {
        background-color: var(--bg-color) !important;
        color: var(--text-color) !important;
    }

    /* Remove shadows and set borders */
    * {
        box-shadow: none !important;
        border-color: var(--border-color) !important;
    }

    /* Style the search bar */
    .gb_Se, .gb_Ae, .gb_Te {
        background-color: var(--bg-color) !important;
        border: 1px solid var(--border-color) !important;
        color: var(--text-color) !important;
    }

    /* Style icons */
    .aXb svg, .aMS svg, .bkI svg, .gb_Ee svg, .gb_Ac svg {
        fill: var(--text-color) !important;
    }

    /* Style checkboxes */
    .T-Jo-auh, .T-Jo-auh::before {
        border-color: var(--border-color) !important;
    }

    /* Chat function styling */
    .aKk, .aKl, .aKm, .ajl, .aju, .aji, .aGy, .ajA, .aic {
        background-color: var(--bg-color) !important;
        color: var(--text-color) !important;
    }

    /* Online status indicator */
    .aGv, .aT5-aOt-I {
        background-color: var(--text-color) !important;
    }

    /* Replace the Google logo with ASCII art */
    .gb_Sc::before {
        content: " _____                 _      \A|  __ \\               | |     \A| |  \\/ ___   ___   __ _| | ___ \A| | __ / _ \\ / _ \\ / _` | |/ _ \\\A| |_\\ \\ (_) | (_) | (_| | |  __/\A \\____/\\___/ \\___/ \\__, |_|\\___|\A                   __/ |       \A                  |___/        ";
        font-family: monospace;
        font-size: 6px;
        line-height: 1;
        white-space: pre;
        color: var(--text-color);
        display: block;
        padding: 5px;
    }

    /* Replace the Gmail logo with ASCII art */
    .gb_uc::before {
        content: " _____           _ _ \A|  __ \\         (_) |\A| |  \\/ __ _ _ _ _| |\A| | __ / _` | | | | |\A| |_\\ \\ (_| | | | | |\A \\____/\\__,_|_|_|_|_|";
        font-family: monospace;
        font-size: 6px;
        line-height: 1;
        white-space: pre;
        color: var(--text-color);
        display: block;
        padding: 5px;
    }

    /* Hide original logos */
    .gb_uc img, .gb_Sc img {
        display: none !important;
    }

    /* Add a blinking cursor effect after the Gmail ASCII art */
    .gb_uc::after {
        content: "|";
        animation: blink 1s step-end infinite;
        color: var(--text-color);
        font-size: 10px;
        vertical-align: bottom;
        margin-left: 2px;
    }

    /* Blinking cursor animation */
    @keyframes blink {
        0%, 100% { opacity: 1; }
        50% { opacity: 0; }
    }

    /* Ensure contrast for focused elements */
    *:focus {
        outline-color: var(--text-color) !important;
    }
}
