style.css:
@-moz-document domain("youtube.com") {
/* General Background and Text Colors */
  body {
    background-color: #181818 !important;
    color: #ccc !important;
  }

  /* Header and Navigation Bar */
  #container, #masthead-container, #search-container {
    background-color: #202020 !important;
    border-bottom: 1px solid #444 !important;
  }

  /* Sidebar (left panel) */
  #guide, ytd-mini-guide-renderer {
    background-color: #202020 !important;
    border-right: 1px solid #444 !important;
  }

  /* Video Titles */
  a#video-title, h1.title {
    color: #e0e0e0 !important;
    font-family: "Arial", sans-serif !important;
    font-weight: bold !important;
  }

  /* Video Thumbnails */
  ytd-thumbnail {
    filter: brightness(0.9) contrast(1.2) !important;
  }

  /* Buttons (like, subscribe, etc.) */
  ytd-button-renderer, yt-icon-button {
    background-color: #333 !important;
    border-radius: 2px !important;
  }

  /* Video Player */
  #player, #movie_player {
    background-color: #000 !important;
  }

  /* Footer */
  #footer-container {
    background-color: #202020 !important;
    border-top: 1px solid #444 !important;
  }

  /* Old YouTube Style Elements */
  ytd-topbar-logo-renderer {
    filter: grayscale(100%) contrast(0.8) !important;
  }
  
  /* Hide some modern elements */
  #end, #comments, ytd-comments, ytd-live-chat-frame {
    display: none !important;
  }

  /* Adjust spacing */
  #primary, #secondary {
    padding: 10px !important;
  }
}
