style.css:
@-moz-document domain("youtube.com") {
/* Background and General Theme */
    body, #page-manager, ytd-app {
        background-color: #1A1A2E !important; /* Dark purple background */
        color: #E0E0E0 !important; /* Light text color */
    }

    /* Header Bar */
    #masthead-container {
        background-color: #3A0CA3 !important; /* Gothic purple header */
    }

    #logo-icon-container {
        filter: invert(100%) !important; /* Invert logo colors for contrast */
    }

    /* Sidebar */
    ytd-guide-section-renderer, ytd-guide-entry-renderer {
        background-color: #3A0CA3 !important; /* Sidebar background */
        color: #E0E0E0 !important; /* Sidebar text color */
    }

    ytd-guide-renderer {
        border-right: 1px solid #4A4A66 !important; /* Gothic border */
    }

    /* Video Thumbnails */
    ytd-thumbnail, .ytd-thumbnail {
        border-radius: 10px !important; /* Rounded corners */
        box-shadow: 0px 0px 15px #4A4A66 !important; /* Soft gothic shadow */
    }

    /* Video Titles */
    h3, h4, .title, .yt-simple-endpoint {
        color: #C77DFF !important; /* Purple text for video titles */
    }

    /* Buttons */
    .yt-icon-button, #buttons ytd-button-renderer, tp-yt-paper-button, yt-icon {
        background-color: #4A0CA3 !important; /* Dark purple buttons */
        color: #E0E0E0 !important; /* Light text/icons */
    }

    .yt-icon-button:hover, #buttons ytd-button-renderer:hover, tp-yt-paper-button:hover, yt-icon:hover {
        background-color: #5A0CA3 !important; /* Slightly lighter on hover */
    }

    /* Links */
    a.yt-simple-endpoint {
        color: #C77DFF !important; /* Purple links */
    }

    a.yt-simple-endpoint:hover {
        color: #9D4EDD !important; /* Darker purple on hover */
    }

    /* Comments Section */
    ytd-comment-renderer, #comment, #simple-box {
        background-color: #222244 !important; /* Darker purple for comments */
        color: #E0E0E0 !important; /* Light text for comments */
    }

    ytd-comment-renderer #author-text, ytd-comment-renderer #content-text {
        color: #C77DFF !important; /* Purple for comment text */
    }

    /* Footer */
    #footer-container {
        background-color: #3A0CA3 !important; /* Gothic purple footer */
        color: #E0E0E0 !important; /* Light text */
    }

    /* Scrollbar */
    ::-webkit-scrollbar {
        width: 10px;
    }

    ::-webkit-scrollbar-thumb {
        background-color: #4A0CA3;
        border-radius: 10px;
    }

    ::-webkit-scrollbar-thumb:hover {
        background-color: #5A0CA3;
    }

    ::-webkit-scrollbar-track {
        background-color: #1A1A2E;
    }
}
