style.css:
body {
    background-color: #1b1b1b;
    color: #e0e0e0;
    margin: 0;
    padding: 0;
    font-family: Arial, sans-serif;
}

header {
    background: linear-gradient(135deg, #1e1e1e, #3a3a3a);
    color: #ff9800;
    padding: 20px;
    text-align: center;
    border-bottom: 2px solid #ff9800;
}

header h1 {
    font-size: 2.5em;
    letter-spacing: 2px;
    text-transform: uppercase;
    margin: 0;
}

nav {
    background-color: #333;
    padding: 10px;
    display: flex;
    justify-content: center;
    flex-wrap: wrap;
}

nav a {
    color: #ff9800;
    text-decoration: none;
    margin: 5px 15px;
    padding: 10px 20px;
    transition: background 0.3s ease, color 0.3s ease;
}

nav a:hover {
    background-color: #ff9800;
    color: #1b1b1b;
}

/* Main Content */
main {
    padding: 40px 20px;
}

main h2 {
    color: #ff9800;
    font-size: 2em;
    margin-bottom: 20px;
}

main p {
    line-height: 1.6;
    margin-bottom: 20px;
}


footer {
    background-color: #1e1e1e;
    color: #e0e0e0;
    padding: 20px;
    text-align: center;
    border-top: 2px solid #ff9800;
}

footer p {
    margin: 0;
}


button {
    background-color: #ff9800;
    border: none;
    color: #1b1b1b;
    padding: 10px 20px;
    text-transform: uppercase;
    font-weight: bold;
    cursor: pointer;
    transition: background 0.3s ease, color 0.3s ease;
}

button:hover {
    background-color: #333;
    color: #ff9800;
}

::-webkit-scrollbar {
    width: 8px;
}

::-webkit-scrollbar-thumb {
    background-color: #ff9800;
    border-radius: 10px;
}

::-webkit-scrollbar-track {
    background-color: #1b1b1b;
}
