style.css:
/* Global Styles */
body {
    font-family: 'Arial', sans-serif;
    color: #fff;
    background-color: #000; /* Dark background */
    margin: 0;
    padding: 0;
}

/* Header */
header {
    background-color: #cc0000; /* Signature red */
    color: #fff;
    padding: 20px;
    text-align: center;
    border-bottom: 3px solid #333; /* Dark border */
}

/* Navigation */
nav {
    background-color: #222; /* Dark background for nav */
    padding: 10px;
    text-align: center;
}

nav a {
    color: #fff;
    text-decoration: none;
    padding: 10px 20px;
    display: inline-block;
}

nav a:hover {
    background-color: #cc0000; /* Red hover effect */
    color: #fff;
    border-radius: 5px;
}

/* Main Content */
main {
    padding: 20px;
    background-color: #222; /* Dark background */
    color: #fff;
}

/* Buttons */
button {
    background-color: #cc0000; /* Red buttons */
    color: #fff;
    border: none;
    padding: 10px 20px;
    border-radius: 5px;
    cursor: pointer;
    font-size: 16px;
}

button:hover {
    background-color: #a30000; /* Darker red on hover */
}

/* Footer */
footer {
    background-color: #cc0000; /* Same red as header */
    color: #fff;
    padding: 10px;
    text-align: center;
    border-top: 3px solid #333; /* Dark border */
}

/* Headings */
h1, h2, h3, h4, h5, h6 {
    font-family: 'Georgia', serif; /* Vintage font style */
    color: #cc0000; /* Red color for headings */
}

/* Paragraphs */
p {
    line-height: 1.6;
}

/* Links */
a {
    color: #cc0000; /* Red links */
    text-decoration: none;
}

a:hover {
    text-decoration: underline;
}

