style.css:
@-moz-document domain("www.bandlab.com") {
/* Fondo de la página en negro */
  body {
    background-color: #000000 !important; /* Fondo negro */
    background-image: url('https://st3.depositphotos.com/1400069/37649/i/450/depositphotos_376490520-stock-photo-abstract-black-background-blank-concrete.jpg');
    background-size: cover;
    background-attachment: fixed;
    color: #FFFFFF !important; /* Texto en blanco para contraste */
  }

  /* Botones generales en rojo */
  button, .btn, .button {
    background-color: #FF0000 !important; /* Rojo brillante */
    color: #FFFFFF !important; /* Texto en blanco */
    border-radius: 8px !important; /* Bordes redondeados */
    border: none !important; /* Sin borde visible */
    padding: 10px 20px !important; /* Tamaño de los botones */
    font-weight: bold !important; /* Texto en negrita */
    transition: background-color 0.3s ease; /* Transición suave */
  }

  /* Efecto hover para botones */
  button:hover, .btn:hover, .button:hover {
    background-color: #B22222 !important; /* Rojo más oscuro al pasar el ratón */
  }

  /* Enlaces */
  a {
    color: #FF0000 !important; /* Enlaces en rojo */
    text-decoration: none !important; /* Sin subrayado */
  }

  a:hover {
    color: #B22222 !important; /* Enlaces en rojo oscuro al pasar el ratón */
  }

  /* Otros elementos de interfaz como campos de entrada de texto */
  input[type="text"], input[type="email"], input[type="password"], textarea {
    background-color: #FFFFFF !important; /* Fondo de los campos de texto en blanco */
    border: 2px solid #FF0000 !important; /* Bordes en rojo */
    color: #333333 !important; /* Texto en color oscuro */
    border-radius: 5px !important; /* Bordes redondeados */
    padding: 8px !important; /* Espacio interior de los campos */
  }
}
