style.css:
@-moz-document domain("youtube.com") {
/* Background */
    body, #content {
        background-image:url('https://wallpapercave.com/wp/WjY21KO.jpg') !important;
        background-size: cover !important;
        background-attachment: fixed !important;
        background-repeat: no-repeat !important;
        color: #e0e0e0 !important; /* Light gray text */
    }

    /* Semi-transparent background for content areas */
    #primary, #secondary, #header, #masthead-container, #guide-content, ytd-comments {
        background-color: rgba(18, 0, 38, 0.95) !important; /* Dark purple with slight transparency */
        backdrop-filter: blur(10px) !important; /* Stronger blur effect */
        border-radius: 12px !important; /* Smooth rounded corners */
    }

    /* Font Style */
    body, h1, h2, h3, .ytd-video-primary-info-renderer, .ytd-comment-renderer {
        font-family: 'Roboto', Arial, sans-serif !important;
        font-weight: 400 !important;
        color: #f5a623 !important; /* Warm yellow color for text */
    }

    /* Links and Buttons */
    a, .yt-simple-endpoint {
        color: #00d4ff !important; /* Neon cyan for links */
        text-decoration: none !important;
        transition: color 0.3s ease-in-out;
    }
    a:hover {
        color: #ff1493 !important; /* Bright pink on hover */
    }

    /* Video Thumbnails */
    ytd-thumbnail, ytd-thumbnail-overlay-time-status-renderer {
        border-radius: 10px !important;
        box-shadow: 0 4px 15px rgba(0, 212, 255, 0.6) !important; /* Neon cyan shadow */
        transition: transform 0.2s ease-in-out;
    }
    ytd-thumbnail:hover {
        transform: scale(1.05) !important; /* Slight zoom on hover */
    }

    /* Video Progress Bar */
    .ytp-progress-bar-container {
        background: rgba(255, 20, 147, 0.4) !important; /* Pink background */
    }
    .ytp-swatch-background-color {
        background-color: #ff1493 !important; /* Bright pink progress bar */
    }

    /* Sidebar */
    #guide-content {
        background-color: rgba(18, 0, 38, 0.85) !important; /* Dark purple sidebar with transparency */
        color: #e0e0e0 !important;
        border-right: 2px solid #f5a623 !important; /* Warm yellow border */
    }

    /* Comments */
    .ytd-comment-renderer {
        background-color: rgba(18, 0, 38, 0.95) !important; /* Dark purple comment background */
        border-radius: 8px !important;
        padding: 10px !important;
        margin-bottom: 10px !important;
        border-left: 3px solid #00d4ff !important; /* Neon cyan accent */
    }

    /* Button Icons */
    .ytp-button {
        filter: hue-rotate(90deg) brightness(1.2) !important; /* Light cyan filter */
    }

    /* Animations for Hover Effects */
    .ytp-chrome-controls, .ytp-button, .ytp-play-button, .ytp-volume-panel, .ytp-settings-button {
        transition: background-color 0.3s ease-in-out !important;
    }
    .ytp-button:hover {
        background-color: rgba(255, 20, 147, 0.7) !important; /* Bright pink on hover */
        filter: hue-rotate(0deg) brightness(2) !important; /* Reset hue and increase brightness */
    }

    /* Header */
    #masthead-container {
        background-color: rgba(18, 0, 38, 0.95) !important; /* Dark purple header */
    }
}
