style.css:
/* Застосовується до всіх веб-сторінок */
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("http"), url-prefix("https") {
/* Робимо фон сторінок темно-сірим */
    body, html {
        background-color: #2b2b2b !important;
        color: #f0f0f0 !important;
    }

    /* Робимо фон блоків та інших елементів також темно-сірим */
    div, section, header, footer, aside, main, article, nav, p, span, li, td, th, input, textarea, select, button {
        background-color: #2b2b2b !important;
        color: #f0f0f0 !important;
        border-color: #444 !important;
    }

    /* Скасування стилів для посилань */
    a {
        color: #a8d1ff !important;
    }

    /* Для кнопок і форм */
    button, input, textarea, select {
        background-color: #444 !important;
        color: #f0f0f0 !important;
        border-color: #666 !important;
    }

    /* Додаткові налаштування для посилань */
    a:visited {
        color: #d1a8ff !important;
    }

    a:hover, a:active {
        color: #ffffff !important;
    }

    /* Забезпечуємо контрастність тексту в заголовках */
    h1, h2, h3, h4, h5, h6 {
        color: #f0f0f0 !important;
    }
}
