style.css:
@-moz-document domain("intertechnic.deliverynote.com") {
/* all pages */
body {                                        /* increase standard font size throughout to make readable */
    font-size: 14px;
}

.lt {                                         /* extend depth of click area for Intertechnic logo to match other menu options */
    padding-bottom: 16px;
}

    .lt:hover {                               /* add hover highlight to Intertechnic logo, consistent with other menu options */
        background: url('https://intertechnic.deliverynote.com/images/hoverbg.png') repeat-x bottom;
    }

input[type="button"]:not([name=btnUpload]):hover,
input[type="submit"]:hover,
#maincontent a:hover,
.sub-menu li:hover {                          /* highlight buttons and links on hover */
    background: gold !important;
    color: black;
}

input[type=submit], input[type=button] {      /* put inset borders around ALL buttons */
    border-style: inset;
}

.modal h3 + button {                          /* change "close" button on modal dialogs so all consistent */
    top: -11px !important;
    right: -9px !important;
    width: 21px !important;
    height: 22px !important;
    color: transparent !important;
    background: url('https://intertechnic.deliverynote.com/images/redcross.gif') no-repeat;
    background-position-x: 3px;
    padding-left: 13px !important;
    background-position-y: 3px;
}

/************************************************************************************/
/* following is for job edit page */
.jobspage textarea,
.jobspage .line div,
.jobspage .line select,
.jobspage .line input:not([type=checkbox]) {                /* use standard font, including for textareas */
    font-size: 12px;
    font-family: helvetica, arial, tahoma, sans-serif;
    min-height: 20px;
}

.jobspage input[type=text], 
.jobspage textarea, 
.jobspage select {                                     /* put some padding into input fields for readability*/
    margin-top: 2px;
    padding-top: 3px;
    padding-bottom: 3px;
}

.jobspage div.line div.dupe,
.jobspage div#hdr div.dupe {                           /* hide buggy "duplicates" link on vehicle row + header */
    max-width: 0px !important;
    display:none;
    margin-right: 0px !important;
}

    .jobspage .line .dupe img[src*='/images/tick.png'] { /* hide tick as it doesn't mean vehicle is OK! */
        display: none;
    }

    .jobspage .line .dupe img { /* move the close icon to top right of dupe list*/
        cursor: pointer;
        margin-top: 2px;
        position: relative;
        right: -242px;
        top: 8px;
        z-index: 1001;
        border: 2px solid red;
        border-radius: 50%;
        padding: 2px;
        background-color: white;
    }

        .jobspage .line .dupe img:hover {               /* highlight on mouseover */
            background-color: red;
            border: 2px solid white;
        }

    .jobspage .line .dupe .joblist {                    /* make whole div a "heading" and wordwrap based on width */
        width: 242px;
        position: absolute;
        top: 20px;
        left: 0px;
        border: 1px solid #000;
        padding: 10px;
        z-index: 1000;
        text-align: left;
        padding: 5px 5px 0px 5px;
        color: var(--heading-colour);
        background-color: var(--heading-bg-colour);
        border-top-left-radius: 12px;
        border-top-right-radius: 12px;
        overflow-wrap: break-word;
        text-wrap: normal;
        text-align: center;
        font-size: 16px !important;
    }

        .jobspage .line .dupe .joblist a {              /* revert to normal size font, white background and nullify padding */
            background-color: white;
            width: 100%;
            display: block;
            padding-bottom: 5px;
            padding-left: 10px;
            margin-left: -5px;
            font-size: 12px;
            text-align: left;
        }

            .jobspage .line .dupe .joblist a:last-of-type { /* nullify bottom padding */
                padding-bottom: 10px;
            }

        .jobspage .line .dupe .joblist br {              /* remove line breaks from jobslist so lines are contiguous for background purposes */
            display: none;
        }

.jobspage .line .act_mileage {                           /* align actual mileage box properly */
    border: 1px rgb(118,118,118) solid;
    margin-top: 2px;
    min-height: 23px;
}

.jobspage #btn_capability {                              /* consistent spacing of buttons */
    margin-left: 8px;
}

.jobspage .line .vstatus {                               /* increase depth of vehicle line to properly border and highlight */
    padding: 3px 0 0 0 !important;
    margin-top: 2px;
    margin-bottom: 3px;
}

    .jobspage .line .vstatus a[style*='color: #cccccc'] {  /* make "unplanned" status text clearer */
        color: #888 !important;
    }

.jobspage div.vselect input[type="checkbox"] {          /* adjust alignment of checkbox */
    margin-top: 1px;
}

.jobspage div#maincontent {                             /* put padding around entire content, so text not hard up against edge of screen */
    padding-left: 2px;
    padding-right: 2px;
    width: calc(100% - 20px);
    margin: 0px 10px 0px 10px;
}

.jobspage #custnotes {                                  /* even out spacing */
    top: 30px;
}

.jobspage .customeremail {
    display: none;
}

.jobspage .expheader .exptitle {
    left: -120px;
}

.jobspage .expheader div {
    background-color: transparent;
}

.jobspage .jobspage .expenses {                         /* remove rounded corners on expense block */
    border-radius: 0px;
    width: 1000px;
}

.jobspage #jobdetails > select + label + input + a > img { /* use standard icon for print */
    display: none;
}

.jobspage #jobdetails > select + label + input + a {    /* use standard icon for print */
    min-width: 22px;
    min-height: 22px;
    background-image: url('https://intertechnic.deliverynote.com/images/print-tiny.png');
    background-repeat: no-repeat;
}

.jobspage .recalcmileages {                             /* align recalc mileage link and increase font size */
    margin: 0px 0px 0px 117px;
    font-size: inherit;
}

.jobspage #jobdetails label {
    width: 100px;
}

.jobspage .claims a,                                    /* put add claim button centrally within row */
.jobspage .claimsred a {
    padding: 3px 15px 3px 15px !important;
}

.jobspage #btnApplyTemplate {                           /* even out button spacing */
    margin-left: 8px;
    margin-top: 3px;
}

.jobspage div.expline {                                 /* ensure each expense line on its own row*/
    clear: both;
}

.jobspage input.DeliveryMileage,                        /* right align numeric and currency fields */
.jobspage div.expcharge input,
.jobspage div.expamount input,
.jobspage div.price input,
.jobspage div.subconrate input {
    text-align: right;
}

.jobspage div.expsave {                                 /* remove extraneous and misleading SAVE button as all fields auto-save*/
    display: none;
}

.jobspage a.expdelete {                                 /* adjust position of expense delete icon */
    float: left;
    position: relative;
    right: 0px;
}

.jobspage .expdriver input {                            /* fix spacing between inputs on expense line */
    width: 100%;
    box-sizing: border-box;
    margin-bottom: 1px;
}

.jobspage #Notes, 
.jobspage #InvoiceNotes {                               /* Increase available space for notes in page top heading area */
    width: 549px;
    margin-bottom: 6px;
}

.jobspage #jobdetails #Status + label + input {         /* consistent width of fields in block */
    width: 164px;
}

.jobspage div.collection_notes textarea, 
.jobspage div.delivery_notes textarea {                 /* Enlarge collection + delivery notes fields */
    width: 140px;
    height: 144px;
}

.jobspage #hdr .vcdate {                                
    margin-left: 118px;
}

.vcdate input, .vddate input {
    border: 1px rgb(118, 118, 118) solid;
    text-align: center;
}

.jobspage .partactions input[type=button], 
.jobspage .partactions input[type=submit] {         /* consistent spacing of buttons in "part actions" area */
    margin-bottom: 5px;
    margin-right: 5px;
}

.jobspage #notesection {                            /* optimise space in top notes section */
    overflow: hidden;
    margin-top: 2px;
    height: 49px;
}

    .jobspage #notesection iframe {                /* adjust position of notes iframe in top notes section */
        overflow: auto;
        height: 86px !important;
        padding: 1px;
        margin-top: -28px !important;
    }

.jobspage .addnote input,
.jobspage .addexpense input,
.jobspage .customeremaillink {
    font-size: inherit !important;                /* standardise size of fonts in text action links */
}
/* one day when CSS catches up....
#uploadsection a:after {
    content: ' ';
    border: 2px solid blue;
    position: relative;
    display: block;
    width: 200px;
    height: 200px;
    background-image: attr(href url);
    background-size: cover;
    color: transparent;
} */

.jobspage .modal h3 {                                           /* rounded top borders of modal popups */
    border-top-left-radius: 12px !important;
    border-top-right-radius: 12px !important;
    font-size: 16px;
    font-weight: normal;
}

/*.jobspage .modal .capability_description:after {
    content: URL('/images/icon_bin.gif') !important;
    position: relative;
    width: 20px;
    height: 20px;
}*/

.jobspage img.capability_delete {                               /* replace cross with standard delete icon */
    height: 1px !important;
    width: 1px !important;
    padding: 14px;
    background: URL('https://intertechnic.deliverynote.com//images/icon_bin.gif') no-repeat;
}

.jobspage div.capability_delete {                               /* and position appropriately*/
    text-align: right;
}

.jobspage .addoptions input[type="checkbox"] {                  /* hide "same collection address" checkbox when adding vehicles */
    display: none;
}
.jobspage div.addoptions {                                      /* hide "same collection address" text when adding vehicles */
    color:transparent;
    position:relative;
    margin-top:-24px;
}

.jobspage div.addoptions:after {                                /* Overwrit "Vehicles" with "Total vehicles" for clarity of meaning */
    content: 'Total vehicles';
    position: absolute;
    top: 26px;
    left: 53px;
    background-color: #efeeef;
    color:rgb(78,78,78) !important;
}

/************************************************************************************/
/* Following is for job search page. Uses same classname as job edit page, do identify by name of main form */
.jobspage form[name=jobsearchform] a.line div {
    text-wrap: none;
    overflow: hidden;
}

.jobspage form[action='jobs-advancedsearch.asp'] a.line {
    width: calc(100% - 10px);
}

    .jobspage form[action='jobs-advancedsearch.asp'] a.line div.company,
    .jobspage form[action='jobs-advancedsearch.asp'] div#searchhdr div.company {
        width: calc(12% - 5px);
    }

    .jobspage form[action='jobs-advancedsearch.asp'] a.line div.custid,
    .jobspage form[action='jobs-advancedsearch.asp'] div#searchhdr div.custid {
        width: calc(5% - 5px) !important;
    }

.jobspage form[action='jobs-advancedsearch.asp'] div#searchinputs div.companyandcust {
    width: calc(17% - 10px);
}

.jobspage form[action='jobs-advancedsearch.asp'] a.line div.jobref,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchinputs div.jobref,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchhdr div.jobref {
    width: calc(5% - 5px);
}

.jobspage form[action='jobs-advancedsearch.asp'] a.line div.clientref,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchinputs div.clientref,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchhdr div.clientref {
    width: calc(7% - 5px);
}

.jobspage form[action='jobs-advancedsearch.asp'] a.line div.invno,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchinputs div.invno,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchhdr div.invno {
    width: calc(4% - 5px);
}

.jobspage form[action='jobs-advancedsearch.asp'] a.line div.diaryref,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchinputs div.diaryref,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchhdr div.diaryref {
    width: calc(14% - 5px);
}

.jobspage form[action='jobs-advancedsearch.asp'] a.line div.status,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchinputs div.status,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchhdr div.status {
    width: calc(5% - 5px);
}

.jobspage form[action='jobs-advancedsearch.asp'] div#searchinputs #StatusFilter {
    margin-top: 3px;
}

.jobspage form[action='jobs-advancedsearch.asp'] div#searchinputs div.colldate,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchhdr div.colldate {
    width: calc(6% - 5px);
    min-width: 106px;
}

.jobspage form[action='jobs-advancedsearch.asp'] a.line div.colldate {
    width: calc(6% - 20px);
    text-align: right;
    margin-right: 35px;
}

.jobspage form[action='jobs-advancedsearch.asp'] a.line div.driver,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchinputs div.driver,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchhdr div.driver {
    width: calc(7% - 5px);
}

.jobspage form[action='jobs-advancedsearch.asp'] a.line div.regno,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchinputs div.regno,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchhdr div.regno {
    width: calc(5% - 5px);
}

.jobspage form[action='jobs-advancedsearch.asp'] a.line div.chassisno,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchinputs div.chassisno,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchhdr div.chassisno {
    width: calc(5% - 5px);
}

.jobspage form[action='jobs-advancedsearch.asp'] a.line div.collectfrom,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchinputs div.collectfrom,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchhdr div.collectfrom {
    width: calc(12% - 5px);
}

.jobspage form[action='jobs-advancedsearch.asp'] a.line div.deliverto,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchinputs div.deliverto,
.jobspage form[action='jobs-advancedsearch.asp'] div#searchhdr div.deliverto {
    width: calc(12% - 5px);
}


/************************************************************************************/
/* Claims listing page */
.claimspage #claimlist .recordcount {
  border-top: 1px solid #ccc;
    clear: both;
}

/************************************************************************************/
/* drivers search page */
.driverspage a[href*='driverdetails.asp'] {
    display: block;
}

/************************************************************************************/
/* PODs search page */
.podspage .line:hover {
  background:transparent;
}
.podspage .line a:hover {
  background: gold; 
  }
}
