style.css:
@-moz-document domain("intertechnic.deliverynote.com") {
body {
  font-size:14px;
}
textarea, .line div, .line select, .line input {
  font-size:12px;
  font-family: helvetica, arial, tahoma, sans-serif;
  min-height:20px;
}
  .line .act_mileage {
    border: 1px rgb(118,118,118) solid;
    font-size: 12px !important;
  }
div.vselect input[type="checkbox"] {
    margin-top: 1px;
}
div#maincontent {
  padding-left:10px;
  padding-right:10px;
  width: calc(100% - 20px);
}
.expheader .exptitle {
  left: -120px;
}
.expheader div {
  background-color: transparent;
}
.jobspage .expenses {
  border-top-left-radius:0px;
  border-top-right-radius:0px;
  width: 1000px;
 /* border-top:none;*/
}
#jobdetails > select + label + input + a > img {
  display:none;
}
#jobdetails > select + label + input + a {
  min-width:22px;
  min-height:22px;
    background-image:url('https://intertechnic.deliverynote.com/images/print-tiny.png'); 
  background-repeat: no-repeat;
}
div.expline {
  clear:both;
}
input.DeliveryMileage, 
div.expcharge input,
div.expamount input,
div.price input,
div.subconrate input {
  text-align:right;
}
div.expsave {
  display:none;
}
a.expdelete {
  float: left;
  position:relative;
  right:0px;
  }
.line .vstatus {
  margin-top:0px;
  margin-bottom:0px;
  padding:0px;
font-size:12px;
  }
}
